div.script:
(function($) {
	function setFees() {
		var method = jQuery('.payment_method input:checked').val();
		if( method && method.length ) {
			var fees_selector = method.toLowerCase() == 'paypal' ? '.paypal_fees_product' : '.stripe_fees_product';
						
			jQuery('#stripe_fee').html(jQuery( fees_selector ).find('input[name*=".2"]').val());
		}
	}
	// displaying the processing fee on donation forms
	jQuery('body')
		.on('change', '.fees_product input[name*=".2"]', function() {
			setFees();
		})
		.on('click', '.payment_method input', function() {
			setFees();
			setSubmitButton();
		});
})( jQuery );
