        if (isMand == true) {
            $('#dvError').show();
            setErrorFocus = $('#dvError').position().top + ' setErrorFocus';
            setErrorFocusOnTop(setErrorFocus);
            $('#dvError').children().not('.errmsgheading').remove();
            if (isiPhone()) {
                $('#dvError').append('Error');
                $('#dvError').append('
' + strErrorMandatory + '');
                $('.txtErrorHide').focus();
            } else {
                $('#dvError').append('
' + strErrorMandatory + '');
                $('#dvError').focus();
            }
            try {
                sendNewHeight();
            } catch (e) { }
        } else if (isAlert == true) {
            $('#dvError').show();
            setErrorFocus = $('#dvError').position().top + ' setErrorFocus';
            setErrorFocusOnTop(setErrorFocus);
            $('#dvError').children().not('.errmsgheading').remove();
            if (isiPhone()) {
                $('#dvError').append('Error');
                $('#dvError').append('
