        function TranslateDD(ev) {
            if (!ev) { ev = window.event; }
            var Source = ev.srcElement ? ev.srcElement : ev.target;
            if ($(Source).hasClass('lang')) {
                return false;
            }
            $('.translatorDiv').toggle();
            $(document).bind('touchstart click', function (e) {
                if (!e) { e = window.event; }
                var S = e.srcElement ? e.srcElement : e.target;
                if ($(S).parents('#lblLanguage').length > 0 || $(S).attr('name') == "ddlLanguages" || S.className == "translatorDiv" || $(S).parent().attr('Class') == "translatorDiv" || S.className == "transwrapper" || $(S).parent().attr('Class') == "transwrapper") { } else {
                    $('.translatorDiv').hide();
                }
            });
        }

