function HidePopup(elename, isSubmit) {
    $('.' + elename).unbind('keydown');
    $('.popbackgroung').fadeOut('fast', function () {
        $('.' + elename).css('display', 'none');
        $('body').css('overflow', '');
    });
    if (isSubmit == false) {
        $('#hdnSubmit').val(0);
        $('#Submit').focus();
        if ($('#FName:visible').length > 0) {
            $('#FName').focus();
        }
        else if ($('#Email:visible').length > 0) {
            $('#Email').focus();
        }
        return false;
    }
    else {
        $('#hdnSubmit').val(1);
        $('#Submit').click();
        return true;
    }

}

function ShowPopup(elename) {
    var ele = $('
', {
        'class': 'popbackgroung'
    });
    $('body').append(ele);
    $('.' + elename).css('display', 'block');
    $('.popUpReader:visible').focus();
    try {
        setFocusOnTop();
        sendNewHeight();
        $('.' + elename).addClass('isEmbed');
    } catch (e) {
        $('.' + elename).removeClass('isEmbed');
        $('body').css('overflow', 'hidden');
    }
    $('.' + elename).bind('keydown', function (event) {
        trapEscapeKey($(this), event)
    })
    $('.' + elename).bind('keydown', function (event) {
        trapTabKey($(this), event)
    })
}

function trapEscapeKey(obj, evt) {

    // if escape pressed
    if (evt.which == 27) {

        // get list of all children elements in given object
        var o = obj.find('*');

        // get list of focusable items
        var cancelElement;
        cancelElement = o.filter(".cancel")

        // close the modal window
        cancelElement.click();
        evt.preventDefault();
    }

}

function trapTabKey(obj, evt) {

    // if tab or shift-tab pressed
    if (evt.which == 9) {

        // get list of all children elements in given object
        var o = obj.find('*');

        // get list of focusable items
        var focusableItems;
        focusableItems = o.filter('[tabindex]').filter(':visible')

        // get currently focused item
        var focusedItem;
        focusedItem = $(':focus');

        // get the number of focusable items
        var numberOfFocusableItems;
        numberOfFocusableItems = focusableItems.length

        // get the index of the currently focused item
        var focusedItemIndex;
        focusedItemIndex = focusableItems.index(focusedItem);

        if (evt.shiftKey) {
            //back tab
            // if focused on first item and user preses back-tab, go to the last focusable item
            if (focusedItemIndex == 0) {
                focusableItems.get(numberOfFocusableItems - 1).focus();
                evt.preventDefault();
            }

        } else {
            //forward tab
            // if focused on the last item and user preses tab, go to the first focusable item
            if (focusedItemIndex == numberOfFocusableItems - 1) {
                focusableItems.get(0).focus();
                evt.preventDefault();
            }
        }
    }

}
