	$(function(){
		$(window).resize(function(){ jsMediaQueries(); calendarListView(); headlinesSliderSizes(); });

			//HEADLINES SLIDER
			hfCounter = 0;
			slideInterval = "";
			slideContainer = $("#hp-content-bottom .headlines ul");
			//CLONE THE FIRST 3 RECORDS FOR CAROSEL EFFECT
			$('#hp-content-bottom .headlines li').slice(0, 3).clone().appendTo(slideContainer);
			numSlides ='';
			sliderWidth = '';
			slideWidth = '';
			slideHeight = '';
			//HEADLINES SLIDER

        	rsMenu();
			jsMediaQueries();
			dynamicContent();
