            loadIcons();
        	pageListAccordion();
        	useChannelForSection();
        	modEvents();
        	if(hasVideo == ''){
        		mmRotator();
        	} 
            stickyShortcuts();
        	stickyHeader();
        	headlinesSliderSizes();
        	headlinesSlider();
	});	

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
            	var windowHeight = $(window).height();
				if(hasVideo != ''){
                	if("ftrue" != "false" && !isMobile.any()) {
                        $("#gb-page.hp").addClass("video-bg");
                        $("#hp-multimedia-outer").height(windowHeight);
                        $(".video-bg header").css("padding-bottom", windowHeight - 40);
                    }
                }

                $("#gb-sticky-shortcuts-inner").height(windowHeight - 125);


			break;
			case "768":
				if(hasVideo != ''){
					$("#gb-page.hp").addClass("video-bg");
				}
			break;
			case "640":
				if(hasVideo != ''){
					$("#gb-page.hp").addClass("video-bg");
				}
			break;
			case "480":
				if(hasVideo != ''){
					$("#gb-page.hp").addClass("video-bg");
				}
			break;
			case "320":
				if(hasVideo != ''){
					$("#gb-page.hp").addClass("video-bg");
				}
			break;
		}
	}
