/* ------------------------------   STICKY HEADER  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function stickyHeader(){
		$(document).scroll(function() {
			var distanceFromTop = $(this).scrollTop();

			if(distanceFromTop >= 135){
				$("#gb-page").addClass("sticky-header");
			} else {
				$("#gb-page").removeClass("sticky-header");
			}

			plx();
		});
	}

	function plx(){
		var scrolled = $(window).scrollTop();
		$('#plx-bg').css('top',-(scrolled*0.2)+'px');
	}

    function stickyShortcuts(){
    	if($("#gb-page.sp").length){
            //CHECK FOR DISTRICT
            if($(".gb-page.delano").length){
                 $("#gb-sticky-shortcuts-inner").load("/ #gb-sticky-shortcuts-inner #sw-content-container15");
            } else {
                 $("#gb-sticky-shortcuts-inner").load("/delano #gb-sticky-shortcuts-inner #sw-content-container15");
            }
        }

    }

