	function loadIcons(){
		head.ready("globalIcons", function(){
			$("#gb-icons").creativeIcons({
				"iconNum"       : "3", // NUMBER OF ICONS
				"icons"         : [
				["Communication/18.png", "Office365 Login", "http://login.microsoftonline.com", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
				["Communication/20.png", "Contact Us", "/Page/1487", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
				["Classroom/4.png", "Aeries Portal", "/Page/1800", "_blank"]
               ],
				"siteID"        : "4",
				"siteAlias"     : "delano",
				"calendarLink"  : "/Page/2",
				"contactEmail"  : "webmaster@djuhsd.org",
				"allLoaded"     : function(){ stickyIcons(); accessibleIcons(); }
			});
		});
	}

    function stickyIcons(){
    	//STICKY ICONS
		$("#gb-sticky-icons .controller").click(function(e){

			e.preventDefault();
			var clickedObj = $(this);
			if($(clickedObj).parent().hasClass("open")){
				setTimeout(function(){

					$(clickedObj).parent().removeClass("open");

					setTimeout(function(){
						$(clickedObj).parent().removeClass("clicked");
						$(clickedObj).next().attr("aria-hidden","true");
						$(clickedObj).focus();
					}, 250);

				}, 250);
			} else {
				$(clickedObj).parent().addClass("clicked");
				$(clickedObj).next().attr("aria-hidden","false");

				setTimeout(function(){
					$(clickedObj).parent().addClass("open");
					$(clickedObj).next().find("a:first").focus();
				}, 100);
			}

		});

		$("#gb-sticky-icons .controlled").hover(function(e){
			e.preventDefault();

		}, function(){

			var clickedObj = $(this).prev();

			setTimeout(function(){

				$(clickedObj).parent().removeClass("open");

				setTimeout(function(){
					$(clickedObj).parent().removeClass("clicked");
					$(clickedObj).next().attr("aria-hidden","true");
					$(clickedObj).focus();
				}, 250);

			}, 250);
		});

		$(".global-icon, .controller-parent").hover(function(){
			var clickedObj = $(this);
			$(clickedObj).addClass("hover");
			setTimeout(function(){
				$(clickedObj).addClass("animate");
				//$(clickedObj).next().find("a:first").focus();
			}, 100);
		}, function(){
			var clickedObj = $(this);
			$(clickedObj).removeClass("animate");
			setTimeout(function(){
				$(clickedObj).removeClass("hover");
			}, 250);
		});
    }

    function accessibleIcons(){

		var iconText = ['Office365 Login','Contact Us','Aeries Portal']
		$(".global-icon").each(function(index){
			$(this).attr({
				"tabindex":	"0",
				"aria-label":	iconText[index]
			});
		});
	}
/* ------------------------------   ____________________   ------------------------------ */
/* ------------------------------   HP HEADLINES SCROLLER  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */

	function headlinesSliderSizes(){
		//clearInterval(slideInterval);
		numSlides = $("#hp-content-bottom .headlines li").length;

		if($(".lt-1024").length){
			slideWidth = 322;
			sliderWidth = slideWidth * numSlides;

		} else {

			sliderWidth = ($(window).width() * .333333) * numSlides;
			slideWidth = $(window).width() * .333333;	
		}

		slideHeight = slideWidth * .6490;
		$(slideContainer).width(sliderWidth);
		$("#hp-content-bottom .headlines li").width(slideWidth);
		$("#hp-content-bottom").height(slideHeight);

		$(slideContainer).css("left","0px");
		hfCounter = 0;

	}
	function headlinesSlider(){

    	if($("#hp-content-bottom .headlines").length){
        	$("#hp-content-bottom").show();
        }

			if(!$(".auto-slide").length){
				$(slideContainer).addClass("auto-slide");
                $("#hp-content-bottom .headlines ul li a, #hp-content-bottom .headlines ul li h1, #hp-content-bottom .headlines ul li div").attr("tabindex", "-1");
                $("#hp-content-bottom .headlines ul li:lt(3) a.ui-article-thumb" ).attr("tabindex", "0");
				slideTimer();
			}


			//ANIMATE SLIDER ON NAV CONTROL CLICK
			$("#headline-slider-controls button").click(function(){

				//STOP THE TIMER
				clearInterval(slideInterval);
				//IF THE PREVIOUS BUTTON IS CLICKED SLIDE TO THE RIGHT
				if($(this).hasClass("previous")){
					//ONLY SLIDE IF THE FIRST SLIDE IS NOT IN THE FIRST POSITION
					if(hfCounter != 0){
						$(slideContainer).animate({ left: '+='+slideWidth }, "slow", function() {
			    			hfCounter--;
		    			});
					}
				} 

				//IF THE NEXT BUTTON IS CLICKED, SLIDE TO THE LEFT
				if($(this).hasClass("next")){

					$(slideContainer).animate({ left: '-='+slideWidth }, "slow", function() {
						hfCounter++;
						//IF THE CLONED FIRST SLIDE IS IN THE FAR LEFT POSITION, RESET THE SLIDER FOR CAROSEL EFFECT
						if(hfCounter == (numSlides -3) ){
							$(slideContainer).css("left","0px");
							hfCounter = 0;
						} 
					});	
				} 

				//RESTART THE TIMER
				slideTimer();
			});

			//PAUSE ON HOVER
			$(slideContainer).hover(function(){
			//STOP THE SLIDER
			clearInterval(slideInterval);
				}, function(){
				slideTimer();
			});

            $("#hp-content-bottom .headlines li").hover(function(){
            	var slide = $(this);
            	$(slide).addClass("hover");

                setTimeout(function(){
                    $(slide).addClass("visible");
                }, 100);
            }, function(){
            	var slide = $(this);
            	$(slide).removeClass("visible");
                setTimeout(function(){
                    $(slide).removeClass("hover");
                }, 250);
            });


	}

	function slideHeadlines(){
		$(slideContainer).animate({ left: '-='+slideWidth }, "slow", function() {
			hfCounter++;
            $("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
            $("#hp-content-bottom .headlines ul li:eq("+hfCounter+") a.ui-article-thumb").attr("tabindex", "0");
            $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find("a.ui-article-thumb").attr("tabindex", "0");
            $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find("a.ui-article-thumb").attr("tabindex", "0");

			//IF THE CLONED FIRST SLIDE IS IN THE FAR LEFT POSITION, RESET THE SLIDER FOR CAROSEL EFFECT
			if(hfCounter == (numSlides -3) ){
				$(slideContainer).css("left","0px");
				hfCounter = 0;
			} 
		});	
	}

	function slideTimer(){
		slideInterval = setInterval(slideHeadlines, 6000);
	}
