$(divedit).css({"background-color": "#FFF"});
$(tab_the_button).css("marginLeft", "30px");

function Text_Toggle_1(id) {
    var x = document.getElementById(id);
    if (x.innerHTML === 'Digital Educational Resources for Families') {
       x.innerHTML = 'Close Digital Educational Resources for Families';
    } else {
       x.innerHTML = 'Digital Educational Resources for Families';
    }

}

body.script:
    $(document).ready(function () {

    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
