    /**
     * @param toMenuName
     * Name of the menu this personalization panel should be inserted to
     * (data-cox-menu-name attribute value)
     *
     * @param identifier
     * ID attribute value of the script element containing the personalization
     * panel mark-up and event name for promotionalSectionPopulated()
     */
    function addPersonalizationPanel(toMenuName, identifier) {
      // Insert a personalization panel unless one already exists; It's
      // possible for global navigation to load later than TNT
      if ($('.cox-menu-link[data-cox-menu-name="' + toMenuName + '"] .promotional-container .tnt-promotional-container').length == 0) {
        $('.cox-menu-link[data-cox-menu-name="' + toMenuName + '"] .promotional-container').addClass('has-promotional').html($('#tnt-' + identifier + '-pp-mark-up').html());

        // Notify global navigation in case anything else needs tidying
        if (typeof promotionalSectionPopulated != 'undefined') {
          promotionalSectionPopulated({
            "identifier": identifier,
            "verticallyCenter": true
          });
        }
      }
    }
	});

	$(document).ready(function() {
		var shouldShowCookieNotice = true;

		// Grab all the cookies
		var cookies = document.cookie.split(';');

		// Don't show the cookie notice if it's been dismissed
		for (var i = 0; i < cookies.length; i++) {
			var cookie = cookies[i];
			if (cookie.indexOf('hasSeenCookieNotice') != -1) {
				shouldShowCookieNotice = false;
			}
		}

		if (shouldShowCookieNotice) {
      if ($('.cookie-notice-container').length == 0) {
        $('#pf-header').before($('#cookie-notice-mark-up').html());
      }

      // Don't show the cookie notice for 30 days
      var expirationTime = new Date();
      expirationTime.setTime(expirationTime.getTime() + (30 * 24 * 60 * 60 * 1000));
      document.cookie = 'hasSeenCookieNotice=true;expires=' + expirationTime.toUTCString() + ';path=/';

			// Close button
			$('.cookie-notice-close-button').click(function() {
				$('.cookie-notice-container').remove();
			});
		}
	});

