	function stickyHeader() {
		//DOC vars
		var scrolled = $(window).scrollTop();
		var channelLoc = $("#gb-channel-list-outer").position();
		var loc = channelLoc.top+$("#gb-channel-list-outer").outerHeight();
		var pad = $("#gb-channel-list-outer").outerHeight();

		if(scrolled >= loc && scrollLock == 0) {
			$("#gb-channel-list-outer").addClass("fixed-header");
			$("body").css("padding", pad+"px 0 0 0");
			scrollLock = 1;
		} else if(scrolled < loc && scrollLock == 1){
			$("#gb-channel-list-outer").removeClass("fixed-header");
			$("body").css("padding", "0");
			scrollLock = 0;
		}
	}

	function pgScreenSize(){
		getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery(650, 350, 1024);
			logoWidth();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery(467, 250, 768);
			logoWidth();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery(467, 250, 640);
			logoWidth();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery(480, 256, 480);
			appView();
			logoWidth480();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery(650, 350, 320);
			appView();
			logoWidth480();
		}
	}

	function photoGallery(imageWidth, imageHeight, breakPoint) {
		if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
			// PHOTO GALLERY ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
					"imageWidth" 			: imageWidth,
		        	"imageHeight"			: imageHeight,
		        	"efficientLoad"         : "no",
		        	"appHeader"				: "no",
		        	"titles"				: "yes",
		        	"captions"				: "yes",
		        	"showTextPerPhoto"		: "yes",
					"photoMoreLink" 		: "yes",
					"photoMoreLinkText" 	: '',
		        	"mainControls"			: "yes",
		        	"links"					: "yes",
		        	"bullets"				: "yes",
		        	"hoverPause"			: "yes",
		        	"transitionType"		: 'slide',
		        	"transitionSpeed"		: 1,
		        	"allLoaded"				: function() {
                    	//DOC add ADA text for more-link
                        $("#hp-slideshow .rs-photo-gallery-photo-more-link a").prepend('Read More');
		        		//DOC move rotator bullets
		        		$("#hp-slideshow .rs-photo-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-photo-gallery-container"));
		        		//DOC replace rotator button arrows
		        		$("#hp-slideshow .rs-photo-gallery-control").addClass("transition-all animated");
		        		$("#hp-slideshow .rs-photo-gallery-control.back").html('');
		        		$("#hp-slideshow .rs-photo-gallery-control.next").html('');
		        		//DOC show controls on hover 
		        		$("#hp-slideshow").hover(function() {
		        			$("#hp-slideshow .rs-photo-gallery-control").removeClass("fadeOutLeft fadeOutRight");
		        			$("#hp-slideshow .rs-photo-gallery-control.back").addClass("fadeInLeft").css("opacity", 1);
		        			$("#hp-slideshow .rs-photo-gallery-control.next").addClass("fadeInRight").css("opacity", 1);
		        		}, function() {
		        			$("#hp-slideshow .rs-photo-gallery-control").removeClass("fadeInLeft fadeInRight");
		        			$("#hp-slideshow .rs-photo-gallery-control.back").addClass("fadeOutLeft").css("opacity", 0);
		        			$("#hp-slideshow .rs-photo-gallery-control.next").addClass("fadeOutRight").css("opacity", 0);
		        		});
		        		if(breakPoint >= 768) {
			        		$("#hp-slideshow .rs-photo-gallery-photo-desc").hover(function() {
			        			$(".rs-photo-gallery-photo-caption", this).slideToggle(250);
			        		}, function() {
			        			$(".rs-photo-gallery-photo-caption", this).slideToggle(250);
			        		});
		        		}
		        	}
			});
			if($(".sw-special-mode-bar").length && !$("#hp-slideshow div.ui-widget.app.gallery.json").length) {
				$("#hp-slideshow, #hp-slideshow .rs-photo-gallery-images").css("height", "350px");
				$("#hp-slideshow #sw-content-container10").html('





');
			}
		}
	}

	function logoWidth() {
		if($("#gb-logo-container img").length) {
			var imgWidth = $("#gb-logo img").width();
			$("#gb-logo-outer").width(imgWidth);
		}
	}

	function logoWidth480() {
		if($("#gb-logo-container img").length) {
			var imgWidth = $("#gb-logo img").width()*.75;
			$("#gb-logo-outer, #gb-logo-container img").width(imgWidth);
		}
	}

	function appView() {
		//DOC dont add app into accordion if it doesnt have a header
		$(".hp div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});
		$("div.ui-hp div.ui-widget-header").off('click').click(function(){
			if($(this).parent().hasClass("active")) {
				$("div.ui-widget.app").removeClass("active");
			} else {
				var clickedApp = $(this).parent().parent().attr("id");

				$("div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}		

	function useChannelForSection() {
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Departments");
		}
        }

	function dynamicContent() {
		// ********** MYSTART ********** //
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();
		//DOC replace schoolist text
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("Select a School");
		//DOC aria button
        $("#sw-search-button").attr({"role": "button", "aria-label": "Search"});
        //DOC move search
		$("#sw-mystart-search").appendTo(".gb-search");
		//DOC #swsignin-txt-username -- AUTO-FOCUS USERNAME SIGNIN FORM FIELD #swsignin-txt-username
		$("#swsignin-txt-username").focus();

		//SW ACCOUNT DROPDOWN
		$("#sw-mystart-account").on("click", function() {
			$(this).children("#sw-myaccount-list").show();
			$(this).addClass("clicked-state");
		});
		$("#sw-mystart-account, #sw-myaccount-list").hover(function() {}, function() {
			$(this).children("#sw-myaccount-list").hide();
			$(this).removeClass("clicked-state");
			$("#sw-myaccount").blur();
		});
		$('#sw-mystart-account').keydown(function(e) {
			e.stopImmediatePropagation();
			if (e.keyCode == 13) {
				$(this).addClass('clicked-state');
				$('#sw-myaccount-list').show();
			}
		});


		// ********** HEADER ********** //
		//DOC vertical-align elements
		if(getWidth >= 480 && getWidth < 640 || getWidth >= 320 && getWidth < 480) {
			var headerHeight = $("#gb-header-outer").height()-36;
		} else {
			var headerHeight = $("#gb-header-outer").height();
		}
		$("#gb-header .gb-logo, #gb-header .gb-sitename").height(headerHeight);
		if($(".gb-header-right.no-icons").length || $(".gb-header-right.icons-auto-true").length) {
			$(".gb-search").height(headerHeight);
		}
		//DOC global icons toggle
        $(".gb-global-icons-button").on("click keypress", function(e) {
            if(e.keyCode == 13 || e.keyCode == 32 || e.type == "click") {
              $(this).toggleClass("animate");
              $(".toggle-icons").slideToggle(500, function() {
              	if(!$(".toggle-icons").hasClass("open")) {
                	$("#icon-number0").focus();
				}
                $(".toggle-icons").toggleClass("open");
              });
            }
        });

        //--> Remove H1 or Tagline if not in use. Added by Chris 07/31/2017
        if(!true) {
        	$('#gb-sitename h1').remove();
        }
        if(!true) {
        	$('#gb-sitename h2').remove();
        }

		//PREVIEW MODE LOGO
		var logoSrc = jQuery.trim("/cms/lib/CA01930645/Centricity/Template/GlobalAssets/images//District/LVUSD Brand Standard Logo small.png");
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {

		} else {
			$("#gb-logo").append('').removeClass("hidden");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		//DOC hide section with no pages
		$("ul.sw-channel-list li.sw-channel-item").each(function() {
			if($(".sw-channel-dropdown li", this).length == 0) {
				$(this).addClass("no-section");
			}
		});
		//DOC check if channel dropdown will overflow the page
		$(".sw-channel-item").on('mouseenter mouseleave', function (e) {
			if ($('ul', this).length) {
				$(".sw-channel-dropdown", this).show();
				var elm = $('ul:first', this);
				var off = elm.offset();
				var l = off.left;
				var w = elm.width();
				var docH = $("#sw-channel-list-container").height();
				var docW = $("#sw-channel-list-container").width();

				var isEntirelyVisible = (l + w <= docW);

				if (!isEntirelyVisible) {
					$(this).addClass('edge');
				} else {
					$(this).removeClass('edge');
				}
			}
		});

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC move view more links
		$(".more-link, #hp-content-outer .view-calendar-link, .sp .view-calendar-link").each(function() {	
			if($(this).parents(".ui-widget.app").children(".ui-widget-header").children("h1").length) {	
				$(this).clone().appendTo($(this).parents(".ui-widget.app").children(".ui-widget-header").children("h1"));
			} else {
				$(this).parents(".ui-widget.app").children(".ui-widget-header").removeClass("ui-helper-hidden").html("
");
				$(this).clone().appendTo($(this).parents(".ui-widget.app").children(".ui-widget-header").children("h1"));
			}
		});
        //DOC change ream ore text
        $("a.more-link span").text("VIEW ALL");
		$(".app-scroller .view-calendar-link").each(function() {
			$(this).clone().appendTo($(this).parents(".ui-widget.app").children(".ui-widget-footer"));
		});
        //DOC adjust sign in page
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
            $("#swsignin-txt-username").focus();
        }
        //DOC adjust register page
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}
		//DOC adjust search results page
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}
		//DOC add read full story button to each healine
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(".ui-article-description", this).append('READ MORE ');
        		});
        	});
        	$(".app-level-social-rss").append('');

        	//MOD EVENTS
        	$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".upcomingevents .ui-articles");
		if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
			$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
		}
		$(".upcoming-column.left h1.ui-article-title").each(function(){
            var date = $(this).text()
            date = date.match(/.{1,3}/g);
            if(date[1].length == 1) {
                date[1] = "0"+date[1];
            }
            $(this).html(''+date[0]+' '+date[1]);
        });
        $(".view-calendar-link span").text("CALENDAR");

	    //APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
		$("div.ui-widget.app .ui-widget-detail img")
        .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        .not($("div.ui-widget.app.headlines .ui-widget-detail img"))
        .each(function() {
          if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined || $(this).parent('p').attr('style') == 'text-align: center;' ) { // IMAGE HAS INLINE DIMENSIONS
              $(this).css({"width": $(this).attr("width") + "px", "max-width": "100%", "height": "auto", "max-height": $(this).attr("height") + "px"});
          }
        });

		// ********** FOOTER ********** //
		//DOC vertical-align elements
		if(getWidth >= 480 && getWidth < 640 || getWidth >= 320 && getWidth < 480) {
			var footerHeight = $("#gb-footer-outer").height()-36;
		} else {
			var footerHeight = $("#gb-footer-outer").height();
		}
		$("#gb-footer .gb-logo, #gb-footer .gb-sitename, #social-icons").height(footerHeight);

		//DOC get Schoolwires footer links
		var feedbackText = $("div#sw-footer-links li:eq(0) a").text();
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var termsText = $("div#sw-footer-links li:eq(2) a").text();
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var policyText = $("div#sw-footer-links li:eq(1) a").text();
		var copyrightText = $("#sw-footer-outer #sw-footer-copyright").text();

        $(".cs-sw-footer-logo").html($("#sw-footer-logo").html());
		$("#cs-sw-footer-links").html("" + feedbackText + "" + termsText + "" + policyText + "
" + copyrightText + "");

	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	    //SEARCH TEXT
        $("#sw-mystart-search").prepend("Search");
	    $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

