div.script:
	$(function() {
		// Create the dropdown base
		$("").appendTo("nav");
                $('nav>select').attr('title','Mobile Navigation');
		// Create default option "Go to..."
		$("", {
		"selected": "selected",
		"value"   : "",
		"text"    : "Go to..."
		}).appendTo("nav select");
		// Populate dropdown with menu items
		$("ul.sideNav > li").each(function() {

			var li = $(this);

			var hasChildren = li.find("ul"),
			children    = li.find("li");

			if (hasChildren.length) {
				$("", {
				"value": li.find("> a").attr("href"),
				"label": li.find("> a").text()

				}).appendTo("nav select");

				children.each(function() {
					$("", {
					"value": $(this).find("> a").attr("href"),
					"text": " - " + $(this).text()
					}).appendTo("nav select");
				});
			} else {
				$("", {
				"value"   : li.find("> a").attr("href"),
				"text"    : li.text()
				}).appendTo("nav select");
			}
		});
		// To make dropdown actually work
		$("nav select").change(function() {
			window.location = $(this).find("option:selected").val();
		});
	});

