link.script:
if (window != top) { top.location.href = location.href; }
if (top.frames.length != 0) {
    if (window.location.href.replace)
        top.location.replace(self.location.href);
    else
        top.location.href = self.document.href;
}

body.script:
function SBCSetSize() {
    try {
        var sizeX = 1024;
        if (typeof (window.innerWidth) == 'number') {
            sizeX = window.innerWidth;
        } else if (document.documentElement && document.documentElement.clientWidth) {
            sizeX = document.documentElement.clientWidth;
        } else if (document.body && document.body.clientWidth) {
            sizeX = document.body.clientWidth;
        }
        if ((/Android|webOS|iPhone|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) || sizeX < 700) {
            var list = document.getElementsByClassName("sbcheaderbarmenu");
            var i = 0;
            var len = list.length;
            for (; i < len; i++) {
                list[0].className = 'sbcheaderbarmenucell';
            }
            var list2 = document.getElementsByClassName("sbcheaderbarmenurow");
            var i2 = 0;
            var len2 = list2.length;
            for (; i2 < len2; i2++) {
                list2[0].className = 'sbcheaderbarmenurowcell';
            }
            var list3 = document.getElementsByClassName("sbcheaderbarmenutext");
            var i3 = 0;
            var len3 = list3.length;
            for (; i3 < len3; i3++) {
                list3[0].className = 'sbcheaderbarmenutextcell';
            }
            document.getElementById('sbcdesktop1').style.display = 'inline-block';
            document.getElementById('sbcdesktop1').style.visibility = 'visible';
            document.getElementById('sbcdesktop2').style.display = 'none';
            document.getElementById('sbcdesktop2').style.visibility = 'hidden';
            document.getElementById('sbcdesktop3').style.display = 'inline-block';
            document.getElementById('sbcdesktop3').style.visibility = 'visible';
            document.getElementById('sbcdesktop4').style.display = 'none';
            document.getElementById('sbcdesktop4').style.visibility = 'hidden';
            document.getElementById('sbcdesktop5').style.display = 'none';
            document.getElementById('sbcdesktop5').style.visibility = 'hidden';
            document.getElementById('sbcdesktop6').style.display = 'none';
            document.getElementById('sbcdesktop6').style.visibility = 'hidden';
            document.getElementById('sbcdesktop7').style.display = 'inline-block';
            document.getElementById('sbcdesktop7').style.visibility = 'visible';
            document.getElementById('sbcservices1').style.display = 'none';
            document.getElementById('sbcservices1').style.visibility = 'hidden';
            document.getElementById('sbcservices2').style.display = 'inline-block';
            document.getElementById('sbcservices2').style.visibility = 'visible';
            document.getElementById('sbcemail1').style.display = 'none';
            document.getElementById('sbcemail1').style.visibility = 'hidden';
            document.getElementById('sbcemail2').style.display = 'none';
            document.getElementById('sbcemail2').style.visibility = 'hidden';
            document.getElementById('sbcheaderbarmenu').style.backgroundColor = '#C0C0C0';
            document.getElementById('sbcheaderbarmenu').style.backgroundImage = "";
        } else if ((/iPad|Opera Mini/i.test(navigator.userAgent)) || sizeX < 1000) {
            var list = document.getElementsByClassName("sbcheaderbarmenucell");
            var i = 0;
            var len = list.length;
            for (; i < len; i++) {
                list[0].className = 'sbcheaderbarmenu';
            }
            var list2 = document.getElementsByClassName("sbcheaderbarmenurowcell");
            var i2 = 0;
            var len2 = list2.length;
            for (; i2 < len2; i2++) {
                list2[0].className = 'sbcheaderbarmenurow';
            }
            var list3 = document.getElementsByClassName("sbcheaderbarmenutextcell");
            var i3 = 0;
            var len3 = list3.length;
            for (; i3 < len3; i3++) {
                list3[0].className = 'sbcheaderbarmenutext';
            }
            document.getElementById('sbcdesktop1').style.display = 'inline-block';
            document.getElementById('sbcdesktop1').style.visibility = 'visible';
            document.getElementById('sbcdesktop2').style.display = 'inline-block';
            document.getElementById('sbcdesktop2').style.visibility = 'visible';
            document.getElementById('sbcdesktop3').style.display = 'inline-block';
            document.getElementById('sbcdesktop3').style.visibility = 'visible';
            document.getElementById('sbcdesktop4').style.display = 'none';
            document.getElementById('sbcdesktop4').style.visibility = 'hidden';
            document.getElementById('sbcdesktop5').style.display = 'none';
            document.getElementById('sbcdesktop5').style.visibility = 'hidden';
            document.getElementById('sbcdesktop6').style.display = 'none';
            document.getElementById('sbcdesktop6').style.visibility = 'hidden';
            document.getElementById('sbcdesktop7').style.display = 'inline-block';
            document.getElementById('sbcdesktop7').style.visibility = 'visible';
            document.getElementById('sbcservices1').style.display = 'none';
            document.getElementById('sbcservices1').style.visibility = 'hidden';
            document.getElementById('sbcservices2').style.display = 'inline-block';
            document.getElementById('sbcservices2').style.visibility = 'visible';
            document.getElementById('sbcemail1').style.display = 'none';
            document.getElementById('sbcemail1').style.visibility = 'hidden';
            document.getElementById('sbcemail2').style.display = 'inline-block';
            document.getElementById('sbcemail2').style.visibility = 'visible';
            document.getElementById('sbcheaderbarmenu').style.backgroundColor = '#C0C0C0';
            document.getElementById('sbcheaderbarmenu').style.backgroundImage = "url('https://www.sbcounty.gov/login2/images/headerbarbg.jpg')";
        } else {
            var list = document.getElementsByClassName("sbcheaderbarmenucell");
            var i = 0;
            var len = list.length;
            for (; i < len; i++) {
                list[0].className = 'sbcheaderbarmenu';
            }
            var list2 = document.getElementsByClassName("sbcheaderbarmenurowcell");
            var i2 = 0;
            var len2 = list2.length;
            for (; i2 < len2; i2++) {
                list2[0].className = 'sbcheaderbarmenurow';
            }
            var list3 = document.getElementsByClassName("sbcheaderbarmenutextcell");
            var i3 = 0;
            var len3 = list3.length;
            for (; i3 < len3; i3++) {
                list3[0].className = 'sbcheaderbarmenutext';
            }
            document.getElementById('sbcdesktop1').style.display = 'inline-block';
            document.getElementById('sbcdesktop1').style.visibility = 'visible';
            document.getElementById('sbcdesktop2').style.display = 'inline-block';
            document.getElementById('sbcdesktop2').style.visibility = 'visible';
            document.getElementById('sbcdesktop3').style.display = 'inline-block';
            document.getElementById('sbcdesktop3').style.visibility = 'visible';
            document.getElementById('sbcdesktop4').style.display = 'inline-block';
            document.getElementById('sbcdesktop4').style.visibility = 'visible';
            document.getElementById('sbcdesktop5').style.display = 'inline-block';
            document.getElementById('sbcdesktop5').style.visibility = 'visible';
            document.getElementById('sbcdesktop6').style.display = 'inline-block';
            document.getElementById('sbcdesktop6').style.visibility = 'visible';
            document.getElementById('sbcdesktop7').style.display = 'inline-block';
            document.getElementById('sbcdesktop7').style.visibility = 'visible';
            document.getElementById('sbcservices1').style.display = 'inline-block';
            document.getElementById('sbcservices1').style.visibility = 'visible';
            document.getElementById('sbcservices2').style.display = 'none';
            document.getElementById('sbcservices2').style.visibility = 'hidden';
            document.getElementById('sbcemail1').style.display = 'inline-block';
            document.getElementById('sbcemail1').style.visibility = 'visible';
            document.getElementById('sbcemail2').style.display = 'none';
            document.getElementById('sbcemail2').style.visibility = 'hidden';
            document.getElementById('sbcheaderbarmenu').style.textAlign = 'left';
            document.getElementById('sbcheaderbarmenu').style.backgroundColor = '#C0C0C0';
            document.getElementById('sbcheaderbarmenu').style.backgroundImage = "url('https://www.sbcounty.gov/login2/images/headerbarbg.jpg')";
        }
    }
    catch (ex) {
        //alert("sbcheaderbar=" + ex.message);
    }
}
SBCSetSize();
if (window.attachEvent) {
    window.attachEvent('onresize', function () {
        SBCSetSize();
    });
}
else if (window.addEventListener) {
    window.addEventListener('resize', function () {
        SBCSetSize();
    }, true);
}
