function populateLeftNavigationSidebar() {
    var containerId = $("#ctl00_ctl49_g_51e8b46e_34d2_437e_a588_3ef8c7954dc5_hdnContainerID").val();
    if (containerId != '') {
        var url = 'https://www.cdph.ca.gov/Programs/CID/DCDC';
            var listname = 'CDPH Left Navigation MetaData';
            var queryText = "covid19sp";
            var queryColumn = 'Subject';
            var notFoundMsg = 'No Item Found';
            var pageTitle = 'Enfermedad del Coronavirus 2019 (COVID-19)';
            readLeftNavigationSidebar(url, listname, containerId, notFoundMsg, queryText, queryColumn, pageTitle);
        }
    }
