var query = siteURL + "/_api/web/getfilebyserverrelativeurl('/"+ relativePageURL +"')?$select=TimeLastModified";
$.ajax({
    url: query,
    type: "GET",
    dataType: "json",
    headers:{
		"X-RequestDigest": $("#__REQUESTDIGEST").val(),
		"accept": "application/json; odata=verbose",
		"content-type": "application/json; odata=verbose"
    },
	success: function (data) {
		getLastModified(data);
	},
    error: function (data) {
		errorOnLastModified(data.statusText);
	}			
});
function getLastModified(data){
	var date = new Date(data.d.TimeLastModified.replace("T", " ").replace("Z", "").replace(/-/g, "/"));
	var formatteddate = monthNamesLower[date.getMonth()] + " " + date.getDate() + ", " + date.getFullYear();
	$("#spnModifiedDate").text(formatteddate);
}
