div.script:
if (!('placeholder' in document.createElement('input'))) {
	var el = document.getElementById('q');
	var orig = 'Search this site';
	el.value = orig;

	el.onclick = function() {
		if (this.value==orig) this.value='';
	}

	el.onblur = function() {
		if (this.value=='') this.value=orig;
	}
}

function gt(value) {
	if(!value) {
		alert("Empty");
		return;
	}
	var windowResult = false;
	try{
		windowResult = window.open(value);
		if (!!!windowResult) window.location.href=value;
	} catch (e) {
		alert(e);
	}
}

var script = document.createElement('script');
script.src = "https://cdnjs.cloudflare.com/ajax/libs/tota11y/0.1.6/tota11y.min.js";

if(getQueryString('tota11y') === 'true') {
	document.head.append(script);
}	

function getQueryString (field, url) {
	var href = url ? url : window.location.href;
	var reg = new RegExp( '[?&]' + field + '=([^&#]*)', 'i' );
	var string = reg.exec(href);
	return string ? string[1] : null;
}

img.script:
	(function () {
    var url = "/apps/js/google_tag_manager/google_tag_manager.js";
    var gtm = document.createElement('script');
    gtm.type = 'text/javascript';
    gtm.async = true;
    gtm.src = ('https:' == document.location.protocol ? 'https://' : 'http://') + document.location.hostname + url;
		var s = document.body.firstChild;
		s.parentNode.insertBefore(gtm, s);
	})();
