div.script:
function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, gaTrack: true, gaId: 'UA-53284323-1'}, 'google_translate_element');
}

form.onsubmit:
return (function (form) {
    Y.use('squarespace-form-submit', 'node', function usingFormSubmit(Y) {
      (new Y.Squarespace.FormSubmit(form)).submit({
        formId: '57f81f3c579fb3dc20071751',
        collectionId: '',
        objectName: 'footerBlock'
      });
    });
    return false;
  })(this);
