div.script:
// Defer scripts until document is fully ready.
$(document).ready(function () {
    $(document).ready(function () {
        // Selects elements that start with "OfficeHoursInfo"
        $("div[id^='OfficeHoursInfo']").OfficeHoursInfo({});
    });
    // script to get all image tags and set 'shrinkImg' class if image width is > 280px
    var images = document.getElementsByTagName('img');
    for (var i = 0; i < images.length; i++) {
        // check if img's real width is > 280px
        if (images[i].naturalWidth > 280) {
            // make img shrinkable
            try {
                images[i].classList.add('shrinkImg');
            }
            catch (e) {
                // NoOp if classList not extant (IE9) 
            };
        }
    }
});

