body.script:
        window.onload = (function () {

            function init() {

                [].slice.call(document.querySelectorAll('.dr-menu')).forEach(function (el, i) {

                    var trigger = el.querySelector('div.dr-trigger'),
                        icon = trigger.querySelector('span.dr-icon-menu'),
                        label = trigger.querySelector('a.dr-label'),
                        open = false;

                    // Open menu
                    trigger.addEventListener('click', function (event) {
                        if (!open) {
                            el.className += ' dr-menu-open';
                            open = true;
                            document.getElementById('drMenu').style.paddingTop = '20px';
                            document.getElementById('drLabel').style.paddingLeft = '0';
                            //document.getElementById('drLabel').style.color = '#C8DE16';
                            //document.getElementById('pMenu').style.backgroundColor = 'rgba(0,0,0,.7)';    // dsm 8/17
                            //document.getElementById('pMenu').style.backgroundColor = 'transparent';       // dsm 8/17

                            /* for debugging
                            dumpComputedStyles(document.getElementById('pMenu'),null);
                            */
                        }
                    }, false);

                    // Close menu (click on icon)
                    icon.addEventListener('click', function (event) {
                        if (open) {
                            event.stopPropagation();
                            open = false;
                            el.className = el.className.replace(/\bdr-menu-open\b/, '');
                            document.getElementById('drMenu').style.paddingTop = '5px';                     // dsm 8/17
                            document.getElementById('drLabel').style.paddingLeft = '1.5em';
                            //document.getElementById('drLabel').style.color = '#C8DE16';
                            //document.getElementById('pMenu').style.backgroundColor = 'transparent';       // dsm 8/17
                            //document.getElementById('pMenu').style.backgroundColor = 'rgba(0,0,0,.7)';    // dsm 8/17
                            return false;
                        }
                    }, false);

                    // Close menu (click on label)
                    label.addEventListener('click', function (event) {
                        if (open) {
                            event.stopPropagation();
                            open = false;
                            el.className = el.className.replace(/\bdr-menu-open\b/, '');
                            document.getElementById('drMenu').style.paddingTop = '5px';                     // dsm 8/17
                            document.getElementById('drLabel').style.paddingLeft = '1.5em';
                            //document.getElementById('drLabel').style.color = '#C8DE16';
                            //document.getElementById('pMenu').style.backgroundColor = 'transparent';       // dsm 8/17
                            //document.getElementById('pMenu').style.backgroundColor = 'rgba(0,0,0,.7)';    // dsm 8/17
                            return false;
                        }
                    }, false);

                });
            }
            // Intialize page/context menu events
            init();

            // Enable the skip to context menu, if context menu is extant on page
            var cm = document.getElementById('drTrigger');
            if (cm != null) {
                var stcm = document.getElementById('skip2CMenu');
                if (stcm != null) {
                    $('#skip2CMenu').show();
                }
            }

            // Set initial focus at top of page
            var a = document.getElementById("topOfPage");
            if (a != null) {
                $('#topOfPage').focus();
            }

        })();

        // Open Context menu when 'clicked' by user via keyboard return key on Skip2CM item
        function clickSkip2CMenu() {
            var l = document.getElementById('drTrigger');
            l.click();
            l.focus();
        }

