	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		searchText();
		headerCheck();

		var numIcons = '6';
		if(numIcons != 0) {
			$("#sw-mystart-right .sw-mystart-button.popular").show();
		}

        $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        $('.sw-mystart-button a, #gb-logo a').focus(function(){
            $('.sw-mystart-dropdown').removeClass('inner').removeClass('open');
            $('.sw-mystart-dropdown .sw-dropdown').attr('aria-hidden', 'true').hide();
        });
	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); headerCheck(); });

		var translator = 'true';
		if (translator == "false"){
			$("#sw-mystart-right .sw-mystart-dropdown.translate").hide();
			var useTranslate = "no";
		}
		else {
			//addFramesetTranslate();
            $("#sw-mystart-right").creativeTranslate({
                "type": 1, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                "advancedOptions": {
                    "addMethod": "append", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                    "dropdownHandleText": "Translate", // ONLY FOR FRAMESET AND API VERSIONS
                    "useTranslatedNames": true // ONLY FOR FRAMESET AND API VERSIONS - true = TRANSLATED LANGUAGE NAME, false = ENGLISH LANGUAGE NAME
                },
                "translateLoaded": function() {
                	$('.sw-mystart-dropdown.translate').insertAfter($('.sw-mystart-dropdown.schoollist'));
                }
            });
            $('.sw-mystart-dropdown.translate').unbind('focus').focus(function(){
                $(this).click();
            }).keydown(function(e){
                var c = e.which;
                if(!($(this).hasClass('inner'))){
                    if(c == csKeyCodes.down || c == csKeyCodes.enter || c == csKeyCodes.space){
                        e.preventDefault();
                        $(this).addClass('inner');
                        $('.sw-dropdown li a', this).first().addClass('first').focus();
                        $('.sw-dropdown li a', this).last().addClass('last');
                    }
                }
            });
            $('.translate .sw-dropdown a').keydown(function(e){
                var c = e.which;
                var next = $(this).parent().next().children();
                var prev = $(this).parent().prev().children();
                if(c == csKeyCodes.down || c == csKeyCodes.up){
                    e.preventDefault();
                    if(c == csKeyCodes.down && $(this).hasClass('last')){
                        $('.sw-mystart-dropdown.translate').focus().removeClass('inner');
                    } else if(c == csKeyCodes.down){
                        next.focus();
                    } else if(c == csKeyCodes.up && $(this).hasClass('first')){
                        $('.sw-mystart-dropdown.translate').focus().removeClass('inner');
                    } else if(c == csKeyCodes.up) {
                        prev.focus();
                    }
                }
            });
			var useTranslate = "yes";
		}

		var districtHome = 'true';
		if (districtHome == "false"){
			$(".sw-mystart-button.home").hide();
		}
		else {
			$(".sw-mystart-button.home").show();
		}

		if($(".sw-mystart-dropdown.schoollist").length) {
			var useSchoollist = "yes";
		} else {
			var useSchoollist = "no";
		}

        $(".sw-mystart-dropdown").click(function() {
        	$(this).addClass("open");
        });
        $(".sw-mystart-dropdown").focus(function() {
        	$(this).addClass("open");
        });
        $(".sw-mystart-dropdown").mouseleave(function() {
        	$(this).removeClass("open");
        });
        $(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing");
			$(this).addClass("open");
		});
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
		}, function() {
			$(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
			$(this).removeClass("open");
		});
        $(document).off("focus", ".sw-mystart-dropdown");
		$(document).on("focus", ".sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
			$(this).addClass("open");
		});

		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"primaryColor"		: "#2C363E", // DEFAULT - #2C363E
		        "secondaryColor"	: "#222A30", // DEFAULT - #222A30
		        "accentColor"		: "#B5BBBF", // DEFAULT - #B5BBBF
			"menuButtonParent"	: "#sw-mystart-left",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: useSchoollist,
			"showTranslate"		: useTranslate,
			"translateId"		: "none",
             "advancedTranlate": {
             	"useAdvancedList": "yes"
                },
			"showAccount"		: "yes",
			"extraMenuOptions"	: "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});

		$(".sp.column.two").creativePagelistMenu({
			"breakPoint"		: 639, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
			"folderURL"		: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-pagelist-menu/rs-menu-files/",
			"insertionMethod"	: "prepend", // OPTIONS ARE prepend OR append
			"menuBtnText"		: "",
			"useDefaultCSS"		: "yes"
		});

		// GLOBAL ICONS
		var iconNum = ("6" == "8;7;6;5;4;3;2;1;0") ? "8" : "6";
		$("#gb-global-icons").creativeIcons({
		        "iconNum"       : iconNum, // NUMBER OF ICONS
		        "icons"         : [
		                ["Communication/18.png", "Email", "https://login.microsoftonline.com/?whr=travisusd.org", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Classroom/7.png", "Parent Portal", "https://aeries.travisusd.org/parent/", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Site Utility/1.png", "Calendar", "https://www.travisusd.org/Page/2", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		                ["Site Utility/4.png", "Directory", "https://www.travisusd.org/site/Default.aspx?PageType=1&SiteID=4&ChannelID=22&DirectoryType=6", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
