	function headerCheck() {//SET MAX-WIDTH ON SCHOOLNAME BASED ON LOGO UPLOADED
		var windowWidth = $(window).width();
		var checkLogo = 'true';
		var checkName = 'true';

		if(windowWidth >= 1024) {
			//CENTER LOGO AND SEARCH VERTICALLY WITHIN HEADER
			var headerHeight = $("#gb-header-outer").height();
			$("#gb-logo, #gb-search, #gb-search-outer").height(headerHeight);

			var maxWidth = 665;
			if(checkLogo == 'false' && checkName == 'true') {
				$("#gb-sitename-outer").css("max-width","70%");
			}
			if(checkLogo == 'true' && checkName == 'false') {
				$("#gb-logo-outer").css("max-width","70%");
			}
			if(checkLogo == 'true' && checkName == 'true') {
				var logoWidth = $("#gb-logo img").width();
				var nameWidth = maxWidth-logoWidth;
				$("#gb-sitename-outer").css("max-width",nameWidth+"px");
			}
		}
		if(windowWidth >= 768 && windowWidth < 1024) {
			//CENTER LOGO AND SEARCH VERTICALLY WITHIN HEADER
			var headerHeight = $("#gb-header-outer").height();
			if($("#gb-sitename-outer").height() > $("#gb-logo-outer").height()) {
				$("#gb-logo").height(headerHeight - 72);
			}

			var maxWidth = 717;
			if(checkLogo == 'false' && checkName == 'true') {
				$("#gb-sitename-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'false') {
				$("#gb-logo-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'true') {
				var logoWidth = $("#gb-logo img").width();
				var nameWidth = maxWidth-logoWidth;
				$("#gb-sitename-outer").css("max-width",nameWidth+"px");
			}
		}
		if(windowWidth >= 640 && windowWidth < 768) {
			//CENTER LOGO AND SEARCH VERTICALLY WITHIN HEADER
			var headerHeight = $("#gb-header-outer").height();
			if($("#gb-sitename-outer").height() > $("#gb-logo-outer").height()) {
				$("#gb-logo").height(headerHeight - 72);
			}

			var maxWidth = 589;
			if(checkLogo == 'false' && checkName == 'true') {
				$("#gb-sitename-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'false') {
				$("#gb-logo-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'true') {
				var logoWidth = $("#gb-logo img").width();
				var nameWidth = maxWidth-logoWidth;
				$("#gb-sitename-outer").css("max-width",nameWidth+"px");
			}
		}
		if(windowWidth >= 480 && windowWidth < 640) {
			//CENTER LOGO AND SEARCH VERTICALLY WITHIN HEADER
			var headerHeight = $("#gb-header-outer").height();
			if($("#gb-sitename-outer").height() > $("#gb-logo-outer").height()) {
				$("#gb-logo").height(headerHeight - 72);
			}

			var maxWidth = 429;
			if(checkLogo == 'false' && checkName == 'true') {
				$("#gb-sitename-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'false') {
				$("#gb-logo-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'true') {
				var logoWidth = $("#gb-logo img").width();
				var nameWidth = maxWidth-logoWidth;
				$("#gb-sitename-outer").css("max-width",nameWidth+"px");
			}
		}
		if(windowWidth >= 320 && windowWidth < 480) {
			//CENTER LOGO AND SEARCH VERTICALLY WITHIN HEADER
			var headerHeight = $("#gb-header-outer").height();
			if($("#gb-sitename-outer").height() > $("#gb-logo-outer").height()) {
				$("#gb-logo").height(headerHeight - 72);
			}

			var maxWidth = 269;
			if(checkLogo == 'false' && checkName == 'true') {
				$("#gb-sitename-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'false') {
				$("#gb-logo-outer").css("max-width","100%");
			}
			if(checkLogo == 'true' && checkName == 'true') {
				var logoWidth = $("#gb-logo img").width();
				var nameWidth = maxWidth-logoWidth;
				$("#gb-sitename-outer").css("max-width",nameWidth+"px");
			}
		}
	}

	function contentFocusRegion() {
		if($("#hp-content-top .ui-widget.app").size() > 1) {
			var $activeHeader = "

";

			$("#hp-content-top").addClass("focus-region");
			$("#hp-content-top #sw-content-container1.ui-hp").tabBlocks();
			$($activeHeader).prependTo("#hp-content-top #sw-content-container1.ui-hp");
			$(".tab-button.active").clone().prependTo("#tab-active-header");

			$("#tab-active-header").click(function() {
				$(this).toggleClass("open");
				$("div.tabs-container").slideToggle();
                if($(this).hasClass("open")) {
                	$(".tabs-container").attr("aria-hidden","false");
                }
			});

            $('#tab-active-header').on('keydown', function(e) { 
            // Trigger the click event from the keyboard 
              var code = e.which; 
              // 13 = Return, 32 = Space 
              if ((code === 13) || (code === 32)) { 
              	$(this).toggleClass("open");
				$("div.tabs-container").slideToggle();
                $(".tab-button").attr("tabindex","0");
                $("div.tabs-container .tab-button:eq(1)").focus();
              }
            });

			$(".tab-button").click(function() {
				$("#tab-active-header .tab-button").hide();
				$(this).clone().prependTo("#tab-active-header");
				$("#tab-active-header .tab-button:eq(0)").show();
				$("div.tabs-container").slideToggle();
				$("#tab-active-header").toggleClass("open");
				getHTML = $("#hp-content-top #sw-content-container1").html();
				createCookie("focusApp",getHTML);
			});

            $('.tab-button').on('keydown', function(e) { 
            // Trigger the click event from the keyboard 
              var code = e.which; 
              // 13 = Return, 32 = Space 
              if ((code === 13) || (code === 32)) { 
                $("#tab-active-header .tab-button").hide();
				$(this).clone().prependTo("#tab-active-header");
				$("#tab-active-header .tab-button:eq(0)").show();
				$("div.tabs-container").slideToggle();
				$("#tab-active-header").toggleClass("open");
				getHTML = $("#hp-content-top #sw-content-container1").html();
				createCookie("focusApp",getHTML);
              }
            });

			if(!readCookie("focusApp")){
				$("#hp-content-top").removeClass("setCookie");

			} else {
				$("#hp-content-top").addClass("setCookie");
				$(getHTML).prependTo("#hp-content-top #sw-content-container1");
			}

		} else {
			$("#hp-content-top").addClass("only-one-app");
			$("#hp-content-top #sw-content-container1.ui-hp div.ui-widget.app").show();
		}
	}

	function createCookie(name,value,days) {
		if (days) {
			var date = new Date();
			date.setTime(date.getTime()+(days*24*60*60*1000));
			var expires = "; expires="+date.toGMTString();
		} else {
			var expires = "";
			document.cookie = name+"="+value+expires+"; path=/";
		}
	}

	function readCookie(name) {
		var nameEQ = name + "=";
		var ca = document.cookie.split(';');
		for(var i=0;i < ca.length;i++) {
			var c = ca[i];
			while (c.charAt(0)==' ') c = c.substring(1,c.length);
			if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
		}
		return null;
	}

	function eraseCookie(name) {
		createCookie(name,"",-1);
	}

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			logoWidth();
			//DOC move mystart buttons
			$(".sw-mystart-dropdown, .sw-mystart-button.home").prependTo("#sw-mystart-right");
			$(".sw-mystart-button.signin, .sw-mystart-button.register, #sw-mystart-account, .sw-mystart-button.pw, #sw-mystart-mypasskey").prependTo("#sw-mystart-left");
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			logoWidth();
			$("#sw-mystart-account, #sw-mystart-mypasskey").prependTo("#sw-mystart-right");
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			logoWidth();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			logoWidth480();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
			logoWidth480();
		}
	}

	function photoGallery(){
		var showSlideshow = 'true';
		if(showSlideshow == 'true') {
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
				// PHOTO GALLERY ROTATOR
				$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
					"imageWidth" 			: 505,
			        	"imageHeight"			: 322,
			        	"appHeader"			: "no",
			        	"titles"			: "yes",
			        	"captions"			: "yes",
			        	"playPause"			: "no",
			        	"mainControls"			: "no",
			        	"links"				: "yes",
			        	"contMoreLink"			: "no",
			       		"contMoreLinkText"		: "Read More",
			        	"descMoreLink"			: "yes",
			        	"descMoreLinkText"		: "MORE",
			        	"bullets"			: "yes",
			        	"bulletControls"		: "yes",
			        	"thumbnails"			: "no",
			        	"thumbnailWidth"		: 122,
			        	"thumbnailHeight"		: 53,
			        	"thumbnailSlideDirection"	: "horizontal", // horizontal or vertical
			        	"hoverPause"			: "yes",
			        	"transitionType"		: 'fade', // fade or slide
			        	"allImagesLoaded"		: function() { },
			        	"onThumbnailLoad"		: function() { },
			        	"allThumbnailsLoaded"		: function() { },
			        	"onTransitionStart"		: function() { },
			        	"onTransitionEnd"		: function() { },
			        	"allLoaded"			: function() { }
				});
				if($(".sw-special-mode-bar").length && !$("#hp-slideshow div.ui-widget.app.gallery.json").length) {
					$("#hp-slideshow, #hp-slideshow .rs-photo-gallery-images").css("height", "322px");
					$("#hp-slideshow #sw-content-container10").html('



');
				}
			}
		} else {
			$("#hp-content-outer").css("margin-top","0");
		}
	}

	function logoWidth() {
		if($("#gb-logo-container img").length) {
			var imgWidth = $("#gb-logo img").width();
			var imgHeight = $("#gb-logo img").height();
			$("#gb-logo-outer").css({"width":imgWidth , "height":imgHeight});
		}
	}

	function logoWidth480() {
		if($("#gb-logo-container img").length) {
			var imgWidth = $("#gb-logo img").width()*.75;
			$("#gb-logo-outer, #gb-logo-container img").width(imgWidth);
		}
	}

	function appView(){
		$("#hp-content-bottom div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#hp-content-bottom #" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function useChannelForSection(){
