div.script:
// MOVE THE OVERFLOW MENU OUTSIDE THE OUTER-WRAP
$('#overflow-menu-53').clone().insertAfter($('.outer-wrap'));
$('#overflow-menu-53').remove();

$(document).ready(function() {

	$('#dropdown-menu-id-53').dropDownMenu({
		format: 'collapse', 
		speed: 250,
		topLevelCaret: true, // Adds the caret to top level nav items.
		topLevelCaretIconInactive: 'fa-angle-down',
		topLevelCaretIconActive: 'fa-caret-down',
		caretIconInactive: 'fa-angle-down', 
		caretIconActive: 'fa-caret-down',
	});
	
	$('#toggle-menu-53').toggleMenu({
		subMenuCaretInactive: 'fa-angle-down',
		subMenuCaretActive: 'fa-caret-down',
		speed: 250,
	});
	
});

