function themifyMobileMenuTrigger(e) {
	var w = document.body.clientWidth;
	if( w > 0 && w <= tf_mobile_menu_trigger_point ) {
		document.body.classList.add( 'mobile_menu_active' );
	} else {
		document.body.classList.remove( 'mobile_menu_active' );
	}

}
