        "ChannelBar": function() {

            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

        },

        "School": function(){

        	var _this = this;
            $(function() {
                var schools = '
' +
                        'ELEMENTARY' +
                        'MIDDLE' +
                        'HIGH' +
                        'OTHER' +
                        'Close school dropdown' + 
                        '',
                    elemSchools = "
",
                    midSchools = "
",
                    highSchools = "
",
                    otherSchools = "
";


                //schools += '
' + schoolList[i].schoolName + '';

                for(var i in schoolList) {
                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        if(schoolList[i].schoolType.toLowerCase() == "elementary") {
                            elemSchools += "
" + schoolList[i].schoolName + "";
                        } else if(schoolList[i].schoolType.toLowerCase() == "middle") {
                            midSchools += "
" + schoolList[i].schoolName + "";
                        } else if(schoolList[i].schoolType.toLowerCase() == "high") {
                            highSchools += "
" + schoolList[i].schoolName + "";
                        } else if(schoolList[i].schoolType.toLowerCase() == "other") {
                            otherSchools += "
" + schoolList[i].schoolName + "";
                        }
                    }
                }

                schools += elemSchools + "" + midSchools + "" + highSchools + "" + otherSchools + "";

                $('#gb-school-dropdown').html(schools);

                $('#gb-school-btn').click(function(){
                    if($(this).hasClass('open')) {
                        $(this).attr('aria-expanded', 'false').removeClass('open');
                        $('#gb-school-dropdown').attr('aria-hidden', 'true').removeClass('open').hide();
                    } else {
                        $(this).attr('aria-expanded', 'true').addClass('open');
                        $('#gb-school-dropdown').attr('aria-hidden', 'false').addClass('open').show();
                   }
                }).keydown(function(e){
                    var c = e.which;
                    if(c == _this.KeyCodes.enter || c == _this.KeyCodes.space || c == _this.KeyCodes.down) {
                        e.preventDefault();
                        if($(this).hasClass('open') && c == _this.KeyCodes.down) {
                            $('#gb-school-dropdown button[aria-selected="true"]').attr('tabindex', '0').focus();
                        } else {
                            $(this).click();
                            $('#gb-school-dropdown button[aria-selected="true"]').attr('tabindex', '-1').focus();
                        }
                    }
                });

                $('#gb-close-schools').click(function(){
                    $('#gb-school-btn').focus().click();
                }).keydown(function(e){
                    var c = e.which; 
                    if(c == _this.KeyCodes.left || c == _this.KeyCodes.up) {
                        e.preventDefault();
                        if(c == _this.KeyCodes.left) {
                            $(this).prev().focus();
                        }
                    }
                });

                $('.gb-school-tab').on('click focus', function(){
                    $('.gb-school-tab').attr('aria-selected', 'false');
                    $(this).attr('aria-selected', 'true');
                    $('.tab-panel').hide().attr('aria-hidden', 'true');
                    $('#' + $(this).attr('aria-controls')).show().attr('aria-hidden', 'false');
                }).keydown(function(e){
                    var c = e.which; 
                    if(c == _this.KeyCodes.left || c == _this.KeyCodes.right || c == _this.KeyCodes.down || c == _this.KeyCodes.up || c == _this.KeyCodes.enter || c == _this.KeyCodes.space) {
                        e.preventDefault();
                        if(c == _this.KeyCodes.right) {
                            $(this).next().focus();
                        } else if(c == _this.KeyCodes.left) {
                            $(this).prev().focus();
                        }
                        if(c == _this.KeyCodes.down || c == _this.KeyCodes.enter || c == _this.KeyCodes.space) {
                            $('.tab-panel[aria-hidden="false"] a').first().focus();
                        } else if(c == _this.KeyCodes.up) {
                            $('#gb-school-btn').focus();
                        }
                    }
                });

                $('.tab-panel a').keydown(function(e){
                    var c = e.which; 
                    if(c == _this.KeyCodes.left || c == _this.KeyCodes.right || c == _this.KeyCodes.down || c == _this.KeyCodes.up || c == _this.KeyCodes.tab) {
                        e.preventDefault();
                        if(c == _this.KeyCodes.right || c == _this.KeyCodes.down) {
                            if($(this).parent().next().html() == undefined) {
                                $('.gb-school-tab[aria-selected="true"]').focus();
                            } else {
                                $(this).parent().next().children().focus();
                            }
                        }
                        if(c == _this.KeyCodes.left || c == _this.KeyCodes.up) {
                            if($(this).parent().prev().html() == undefined) {
                                $('.gb-school-tab[aria-selected="true"]').focus();
                            } else {
                                $(this).parent().prev().children().focus();
                            }
                        }
                        if(c == _this.KeyCodes.tab) {
                            $('.gb-school-tab[aria-selected="true"]').focus();
                        }
                    }
                });
            });

        },

        "Dropdown": function(_this) {

            //--> Dropdown funciton
            var closeDrop;
            var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            $('.gb-mystart-btn.translate, .gb-mystart-btn.options').unbind().click(function(e){

                var dropdown = $(this).attr('aria-controls');

                e.preventDefault();

                if($(this).hasClass('open')) {

                    $('#' + dropdown).removeClass('open').attr('aria-hidden', 'true').stop(true, true).hide();
                    $(this).removeClass('open').attr('aria-expanded', 'false');
                    $('.gb-dropdown a').attr('tabindex', '-1');

                } else if($(this).hasClass('inner')) {

                    $('#' + dropdown).addClass('open').attr('aria-hidden', 'false').show();
                    $(this).addClass('open').attr('aria-expanded', 'true');
                    $('.gb-dropdown a', this).attr('tabindex', '-1').first().focus();
                    if(dropdown == "gb-translate-dropdown") {
                    	$('#cs-branded-translate-label').focus();
                    } else {
                    	$('#gb-account-dropdown a').first().focus();
                    }

                } else {

                    $('#' + dropdown).addClass('open').attr('aria-hidden', 'false').stop(true, true).show();
                    $(this).addClass('open').attr('aria-expanded', 'true');
                    $('.gb-dropdown a').attr('tabindex', '-1');

                }


            }).keydown(function(e){

                var c = e.which;

                if(c == KeyCodes.down){

                    e.preventDefault();
                    $(this).addClass('inner').click();

                }

                if(c == KeyCodes.enter || c == KeyCodes.space ) {

                	e.preventDefault();
                    if(!$(this).hasClass('open')) {
                    	$(this).addClass('inner').click();
                    } else {
                    	$(this).click();
                    }

                }

            }).focus(function(){
            	$('.gb-mystart-btn.translate.open').click();
            });

            $('#gb-mystart-upper .gb-dropdown').unbind().hover(function(){

                clearTimeout(closeDrop);

            }, function(){
                var id = $(this).attr('id').replace('dropdown', 'btn');
				if($(this).hasClass('open')) {

                    closeDrop = setTimeout(function(){

                        $('#' + id).focus().click();

                    }, 300);

                }

            });

            $('#gb-account-dropdown a').unbind().keydown(function(e){

                var c = e.which,
                    first = $(this).parent().first().children(),
                    last = $(this).parent().last().children(),
                    next = $(this).parent().next().children(),
                    prev = $(this).parent().prev().children(),
                    btn = $('#' + $(this).closest('.gb-dropdown').attr('id').replace('dropdown', 'btn'));

                if(c == KeyCodes.down || c == KeyCodes.up || c == KeyCodes.left || c == KeyCodes.right) {

                    e.preventDefault();

                    if(c == KeyCodes.down && next.html() == undefined) {

                        first.focus();

                    } else if(c == KeyCodes.up && prev.html() == undefined) {

                        btn.removeClass('inner').focus();

                    } else if(c == KeyCodes.down) {

                        next.focus();

                    } else if(c == KeyCodes.up) {

                        prev.focus();

                    }

                }

            }).focus(function(){

                clearTimeout(closeDrop);

            });

        },

