                url: '/cms/lib/FL02201431/Centricity/template/globalassets/ads/'+zone+'/'+zone+'.csv',
                type: 'get',
               	dataType:"text",
               	success:function(data) {

                	var row = data.split(/\r?\n|\r/);

                    for(var cell = 0; cell < row.length; cell++) {

                 		var adInfo = row[cell].split(",");

                        if(cell != 0) {
                        	zoneData.push( adInfo[1] + ", " + adInfo[2] + ", " + adInfo[3]);
