        "Search": function() {
        	var _this = this;

            //SEARCH TEXT
            //$('#sw-search-button').remove();
            $('#sw-search-button').attr('aria-hidden', 'true').addClass('hidden');
            $('#sw-mystart-search').prependTo($('#gb-search-outer'));
            $("input#sw-search-input").attr('placeholder', 'Search...').attr('tabindex', '-1').attr('aria-hidden', 'true');

         	$("input#sw-search-input").attr("value", "Search...");
            $("input#sw-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            });
            $("input#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }

                $('#gb-mystart-upper button:not("#gb-search-btn")').attr('tabindex', '0').attr('aria-hidden', 'false');
                	$('#gb-search-btn').removeClass('open').attr('aria-label', 'Open Search');
                    $('#gb-mystart-upper #sw-search-input').removeClass('open').attr('aria-hidden', 'true').attr('tabindex', '-1');

            });


            $('#gb-search-btn').click(function(){
            	if($(this).hasClass('open')) {
                	$('#gb-mystart-upper button:not("#gb-search-btn")').attr('tabindex', '0').attr('aria-hidden', 'false');
                	$(this).removeClass('open').attr('aria-label', 'Open Search');
                    $('#gb-mystart-upper #sw-search-input').removeClass('open').attr('aria-hidden', 'true').attr('tabindex', '-1');
                } else {
                	$('#gb-mystart-upper button:not("#gb-search-btn")').attr('tabindex', '-1').attr('aria-hidden', 'true');
                	$(this).addClass('open').attr('aria-label', 'Close Search');
                    $('#gb-mystart-upper #sw-search-input').addClass('open').attr('aria-hidden', 'false').attr('tabindex', '0').delay(300).focus();
                }
            }).keydown(function(e){
            	var c = e.which;
                if(c == _this.KeyCodes.enter) {
                	e.preventDefault();
                }


            }).keyup(function(e){
            	var c = e.which;
                if(c == _this.KeyCodes.enter) {
                	$(this).click();
                }
            }).focus(function(){
            	$('.gb-mystart-btn.open:not(".search")').click();
            });

        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

