        tabHandle: '.schools_handle',                              //class of the element that will be your tab
        pathToTabImage: '/assets/img/side-tab-schools.png',        //path to the image for the tab (optionaly can be set using css)
        imageHeight: '248px',                                      //height of tab image
        imageWidth: '30px',                               //width of tab image
