body.script:
    $(document).ready(function () {

               
                if (getCookie('AngeloSiteAlertCookie-50cf899f48db48fba83ea8ea4e745bcd-81954efef029440890d79699da5a70a6-20200428-16285144') == "") {
                    $.dialog('/sys/public/sitealerts/display', { alertId: '81954efef029440890d79699da5a70a6' })
                        .done(function () {
                    
                            setCookie('AngeloSiteAlertCookie-50cf899f48db48fba83ea8ea4e745bcd-81954efef029440890d79699da5a70a6-20200428-16285144', "sitealert", 365)
                        });
                }

            
    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
