$(function(){
       var away = false;
    $(document).scroll(function() {
        
        
         if ($(document).scrollTop() > 160) {
            if (!away) {
                away = true;
        $('.mobileHeader').css('top','0px');
                $('.mobileHeader').removeClass('animated slideOutUp');
                $('.mobileHeader').addClass('animated slideInDown');
            }
        } else {
               away = false; 
               $('.mobileHeader').css('top','-300px');
                $('.mobileHeader').removeClass('animated slideInDown');
                $('.mobileHeader').addClass('animated slideOutUp');
        }
  
        
       $('.scrollup').click(function(){
      $('html, body').stop().animate({scrollTop : 0},300);
      return false;
       });

    });
   
    
});

$(window).on("load resize",function(){
    accordionThis();
});
  
function accordionThis(){       
    if ($(window).width() < 950){
        $("#quicklinks .ql-wrapper").addClass("accordion-this");
        $("#quicklinks ul").slideUp('fast');
      }
    else {
      $("#quicklinks .ql-wrapper").removeClass("accordion-this");
        $("#quicklinks ul").slideDown('fast');
      }
}

$(document).ready(function() {
    $('#quicklinks .sec-header').click(function(e) {
      e.preventDefault();
       
      if($(this).parent().hasClass('accordion-this')) {
          $('#quicklinks ul').slideUp();
    
          if($(this).next().is(':hidden')) {
          $(this).next().slideDown(); 
        } else {
            $(this).next().slideUp();
                }
      
    }
   });
});

