body.script:
// May be using a modal/pop-up on the home page. We don't want to see it 
// every time, so set a cookie that it dies off after a day. 
function handle_cookie(cookie_found){
  if(!cookie_found){
    var date = new Date();
    date.setTime(date.getTime() + (24 * 60 * 60 * 1000));
    var expires = "; expires=" + date.toGMTString();
    document.cookie = cookieName+"=1"+expires+"; path=/";

    jQuery('#cboxOverlay').css('visibility','visible');
    jQuery('#colorbox').css('visibility','visible');
    // jQuery('#cboxOverlay').attr('style', jQuery('#cboxOverlay').attr('style') + '; ' + 'display:block !important');
    // jQuery('#colorbox').attr('style', jQuery('#colorbox').attr('style') + '; ' + 'display:block !important');
  }else{
    jQuery('#cboxOverlay').remove();
    jQuery('#colorbox').remove();
  }
}

var cookieName = 'show_modal';
var cookie_found = false;

var cookie_split = document.cookie.split(';');
for(i=0;i<cookie_split.length;i++){
  c = cookie_split[i];
  while (c.charAt(0)==' ') c = c.substring(1,c.length);
  if(c.indexOf(cookieName) == 0){
    cookie_found = true;
    break;
  }
}

