div.script:
$(document).ready(function () {
    $("#9f74d841-aaa5-4574-9a2e-950b7ce74b4c").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 4 > 1 ? autoPlayValue : false;
    }
});

jQuery(document).ready(function () {
    jQuery('#e155337f-9c4e-460e-b46b-c15148756716').hide();
    jQuery('#e155337f-9c4e-460e-b46b-c15148756716').listSolrResults({
        currentPagePath: '/content/schools/idva/en/faqs/coronavirus-faqs',
        label: 'Upcoming Events',
        listFrom: 'solr',
        limit: '5',
        listType: 'event',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/content/schools/idva/en/deactivated/calendar',
        viewAllTitle: 'View All Events',
        uuid: 'e155337f-9c4e-460e-b46b-c15148756716',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

