/*SEARCH CODE*/
function doSearch(event) {
  var keyword = document.getElementById("search").value;
  if (keyword.length > 0) {
    if (event && event.preventDefault) {
      event.preventDefault();
    } else {
      event.returnValue = false;
    }

    window.location = "/search-results?keywords=" + keyword;
  }
}

