link.script:
    /*
     * For Section 504/508 compliance:
     * 1) accessibleSkiplinks(): Show skiplinks when they receive focus.
     * 2) accessibleSubnav(): Make leftnav flyout keyboard- and mouse-accessible.
     */

    $(document).ready(function () {
      $(".skiplinks").accessibleSkiplinks();
      $(".sitenav").accessibleSubnav();
      $(".deflicker").removeClass("deflicker"); // Re-enable content that could flicker during loading.
    });

    $.fn.accessibleSkiplinks = function () {
      var el = $(this);
      $("a", el).addClass("unfocused");
      $("a", el).focus(function () {
        $(this).addClass("focused");
        $(this).removeClass("unfocused");
      }).blur(function () {
        $(this).addClass("unfocused");
        $(this).removeClass("focused");
      });
    };

    $.fn.accessibleSubnav = function () {
      var el = $(this);
      $("li", el).addClass("unfocused");
      $("li", el).mouseover(function () {
        $(this).addClass("focused");
        $(this).removeClass("unfocused");
      }).mouseout(function () {
        $(this).addClass("unfocused");
        $(this).removeClass("focused");
      });

      $("a", el).focus(function () {
        $(this).parents("li").addClass("focused");
        $(this).parents("li").removeClass("unfocused");
      }).blur(function () {
        $(this).parents("li").addClass("unfocused");
        $(this).parents("li").removeClass("focused");
      });

    };

