				$(".widgetSearchButton.widgetSearchButton98e0544f-549e-478f-abb7-75386488b291").click(function (e) {
				e.preventDefault();
					if ($("#ysnSearchOnlyDept98e0544f-549e-478f-abb7-75386488b291").is(':checked')) {
						doWidgetSearch($(this).siblings(".widgetSearchBox").val(), Number(0));
					} else {
						doWidgetSearch($(this).siblings(".widgetSearchBox").val(), 0);
					}
			});
			$("#searchField98e0544f-549e-478f-abb7-75386488b291").keypress(function (e) {
				if (window.clipboardData) {
					if (e.keyCode == 13) {
							if ($("#ysnSearchOnlyDept98e0544f-549e-478f-abb7-75386488b291").is(':checked')) {
								doWidgetSearch($(this).val(), Number(0));
							} else {
								doWidgetSearch($(this).val(), 0);
							}
						return false;
					}
				} else {
					if (e.which == 13) {
							if ($("#ysnSearchOnlyDept98e0544f-549e-478f-abb7-75386488b291").is(':checked')) {
								doWidgetSearch($(this).val(), Number(0));
							} else {
								doWidgetSearch($(this).val(), 0);
							}
						return false;
					}
				}
				return true;
			});

				if (true) {
                    var currentRequest = null;
			$("#searchField98e0544f-549e-478f-abb7-75386488b291").autocomplete({
				    source: function (request, response){
                                currentRequest = $.ajax({
				                    url: '/Search/AutoComplete',
				                    dataType: "json",
				                    timeout: 10000,
				                    beforeSend : function()    {
				                        if(currentRequest != null) {
				                            currentRequest.abort();
				                        }
				                    },
				                    data:
                                    {
				                        term: request.term,
				                    },
				                    success: function (data)
				                    {
                                        response(data);
                                        $('.autoCompleteError').remove();
				                    },
				                    error: function(xmlhttprequest, textstatus, message){
                                     if(textstatus==="timeout")
				                     {
                                        if($("#searchField98e0544f-549e-478f-abb7-75386488b291").siblings('.autoCompleteError').length == 0)
                                            $('
Search autocomplete is currently not responding. Please try again later.').insertAfter($("#searchField98e0544f-549e-478f-abb7-75386488b291"));
				                     }
				                    }
				                });
				            },
				html: true,
							delay: 500,
				select: function (event, ui) {
					$(this).val(ui.item.value);
					$(this).next().click();
				}
			}).data("autocomplete")._renderItem = function (ul, item) {
				return $("
")
					.data("item.autocomplete", item)
					.append("" + item.label + "")
					.appendTo(ul);
			};
}
}
			catch(e) {} //we're going to eat this error. Autocomplete won't work but we dont wan't to break anything else on the page.
