		//display 'None at this time.' message for empty headlines apps 
		$('div.ui-widget.app.headlines > div.ui-widget-detail > ul.ui-articles').each(function(){ 
			if($.trim($(this).text()) == ""){ 
				$(this).html('

None at this time.'); 
			} 
		}); 

		//display 'None at this time.' message for empty announcements apps 
		$('div.ui-widget.app.announcements > div.ui-widget-detail > ul.ui-articles').each(function(){ 
			if($.trim($(this).text()) == ""){ 
				$(this).html('

None at this time.'); 
			} 
		}); 
	});

	$(window).load(function() {
		calendarListView();
		searchText();
		$("div.ui-widget.app.navigation li").each(function() {
        	$("> a", this).after($("> div.bullet", this));        	
        });
	});

	$(function(){
		// SET BROWSER AND VERSION CLASS TO BODY
		$("body").addClass(browserName + " " + browserName + browserVersion);

		$(window).bind("resize", function(){ jsMediaQueries(); calendarListView(); });

		// RESPONSIVE MAIN MENU
		$("body").csRsMenu({
		        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
				"primaryColor"          : "#012c4f", // DEFAULT - #1A1A1A
				"secondaryColor"        : "#003f7e", // DEFAULT - #0C0C0C
				"accentColor"           : "#003f7e", // DEFAULT - #BABABA
		        "menuButtonParent"      : "#gb-header-right",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "MENU",
		        "showSchools"           : "no",
		        "showTranslate"         : "yes",
		        "translateId"           : "none",
		        "showAccount"           : "yes",
		        "extraMenuOptions"      : "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
		        "extraMenuItems"        : "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
		        "siteID"                : "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
                "primaryColor"          : "#012c4f", // DEFAULT - #1A1A1A
                "secondaryColor"        : "#003f7eC", // DEFAULT - #0C0C0C
                "accentColor"           : "#ddb846", // DEFAULT - #BABABA
		        "menuButtonParent"      : "#sp-content",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "4"
		});

		jsMediaQueries();
		useChannelForSection();
		dynamicContent();
		modEvents();
		addFramesetTranslate();
	});

	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				if($(".hp").length){
					photoGallery();
				}
			break;
			case "768":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
			case "640":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
			case "480":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
			case "320":
				if($(".hp").length){
					photoGallery();
					appView();
				}
			break;
		}
	}	

	function photoGallery() {
		if(!$("#sw-content-container14.region.ui-hp .rs-gallery-container").length){
            // MULTIMEDIA ROTATOR
            $("#sw-content-container14.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "imageWidth"              : 870,
                    "imageHeight"             : 490,
                    "efficientLoad"           : "yes",
                    "titles"                  : "no",
                    "captions"                : "yes",
                    "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                    "bullets"                 : "yes",
            });
		}
	}

	function appView() {
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$(".hp.column.three div.ui-widget.app:first").addClass("active");
		}
		$(".app-accordion div.ui-widget-header").click(function(){
			if($(this).parent().hasClass("active")) {
				$("div.ui-widget.app").removeClass("active");
			} else {
				var clickedApp = $(this).parent().parent().attr("id");

				$("div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}		

	function useChannelForSection() {
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function dynamicContent() {
		// ********** MYSTART ********** //
		//DOC move passkeys
		$("#sw-mystart-mypasskey").appendTo($("#gb-header-mystart-inner"));
		//DOC fix spaces for search
		$("#sw-mystart-search").unbind();
		$("ul.sw-channel-list").after($("#sw-mystart-search"));

		// ********** HEADER ********** //
		// AJAX ALERT
		if($(".sp").length) {
		    $.ajax({   
		        url : "/lths",
		        type : "GET",
		        success : function(data) {
                    $("#gb-emergency-outer").append($(data).find("#gb-emergency"));
                    if($("#gb-emergency div.ui-widget.app div.ui-widget-detail .ui-article").length != 0) {
                        $("#gb-emergency-outer").slideToggle(500);
                    }
                    //RESET SYSTEM REDIRECT COOKIE AJAX REQUEST 
			        SetCookie('RedirectTo', window.location.href);
		        }

		    });
		} else {
			if($("#gb-emergency div.ui-widget.app div.ui-widget-detail .ui-article").length != 0) {
				$("#gb-emergency-outer").slideToggle(500);
			}
		}		

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");
		//DOC check if channel dropdown goes off screen
		$(".sw-channel-item").on('mouseenter mouseleave', function (e) {
			if ($('ul', this).length) {
				$(".sw-channel-dropdown", this).show();
				var elm = $('ul:first', this);
				var off = elm.offset();
				var l = off.left;
				var w = elm.width();
				var docH = $("#sw-channel-list-container").height();
				var docW = $("#sw-channel-list-container").width();

				var isEntirelyVisible = (l + w <= docW);

				if (!isEntirelyVisible) {
					$(this).addClass('edge');
				} else {
					$(this).removeClass('edge');
				}
			}
		});
		$("li.sw-channel-item").each(function() {
			if($(".sw-channel-dropdown li", this).length == 0) {
				$(this).addClass("no-section");
			}
		});

