		if($("#ui-mypasskey-overlay").length){
			$("#ui-mypasskey-overlay").css({
				"bottom":"156px",
				"left":"0"
			});
		}
	});

	$(window).load(function() {
		calendarListView();
		searchText();

		//HIDE EMPTY DROPDOWN THAT GETS PLACED BY THE SYSTEM IF CHANNEL HAS NO SECTIONS BUT CHANNEL HOMEPAGE IS TURNED ON
		$("ul.sw-channel-dropdown").each(function() {
			var systemDropdown = $(this);
			if(!$("li",systemDropdown).length) {
				$(systemDropdown).remove();
			}
		});

		$("#hp-content-two .hp.column.one .ui-widget.app.headlines .ui-articles li").each(function() {
			var thisLI = this;

			$(this).hover(function() {
				$("div.ui-article",thisLI).fadeOut();
			}, function() {
				$("div.ui-article",thisLI).fadeIn();
			});
		});
	});

	$(window).scroll(function() {
		if($(".hp").length) {
			openDropdownsUp();
		}
	});

	function openDropdownsUp() {
		var scrollTop = $(window).scrollTop();

		if($("#hp-alert-outer").length) {
			var alertHeight = $("#hp-alert-outer").height();
			alertHeight = alertHeight + 88;
			if(scrollTop < alertHeight) {
				$("#gb-mystart-right").addClass("open-upwards");
			} else {
				$("#gb-mystart-right").removeClass("open-upwards");
			}
		} else {
			if(scrollTop < 88) {
				$("#gb-mystart-right").addClass("open-upwards");
			} else {
				$("#gb-mystart-right").removeClass("open-upwards");
			}
		}
	}

