	function audienceTabs1024() {
		$(".audience-tab").click(function(e) {
			e.preventDefault();
			var myName = $.trim( $(this).attr("class").replace("audience-tab","") );
		}, function() {
			var myName = $.trim( $(this).attr("class").replace("audience-tab","") );
			$(".audience-tab." + myName).removeClass("active");
			$(".audience-info-outer." + myName).removeClass("open").slideUp();
		});
		//USE METAPROGRAMMING TO ELIMINATE CODE DUPLICATION
		$(".audience-tab").hover(function(){
			$(".audience-info-outer").stop(true, true);
			var getHeight = $("#hp-video-outer").height();
			$(".audience-info-outer").css("height",getHeight - 88);
			$(".audience-info-outer.open").removeClass("open").slideUp();
			var myName = $.trim( $(this).attr("class").replace("audience-tab","") );
			$(".audience-info-outer").stop(true, true);
			$(".audience-info-outer." + myName).addClass("open").slideDown();
			$(".audience-info-outer.open." + myName).hover(function() {
				$(".audience-tab").click();
				var getHeight = $("#hp-video-outer").height();
				$(".audience-info-outer").css("height",getHeight - 88);
			}, function() {
				$(".audience-tab").off("click");
				$(".audience-tab." + myName).removeClass("active");
				$(".audience-info-outer." + myName).removeClass("open").slideUp();
			});
			$(".audience-info-outer.open." + myName).hover(function() {
				$(".audience-tab." + myName).addClass("active");
			}, function() {
				$(".audience-tab." + myName).removeClass("active");
				$(".audience-info-outer." + myName).removeClass("open").slideUp();
			});
		}, function() {
			var myName = $.trim( $(this).attr("class").replace("audience-tab","") );
			$(".audience-info-outer").stop(true, true);
			if($(".audience-info-outer").hasClass("open")) {
				$(".audience-tab." + myName).removeClass("active");
				//$(".audience-info-outer." + myName).removeClass("open").slideUp();
				/*$(".audience-info-outer.open." + myName).hover(function() {
					$(".audience-tab." + myName).addClass("active");
				}, function() {
					$(".audience-tab." + myName).removeClass("active");
					$(".audience-info-outer." + myName).removeClass("open").slideUp();
				});*/
			}
		});
	}

	function audienceTabs768() {
		$(".audience-tab").off();
		$(".audience-tab").click(function(e) {
			e.preventDefault();
			var myName = $.trim( $(this).attr("class").replace("audience-tab","") );
			if($(".audience-info-outer." + myName).hasClass("open")) {
				$(".audience-tab." + myName).removeClass("active");
				$(".audience-info-outer." + myName).removeClass("open").slideUp();
			} else {
				$(".audience-tab").removeClass("active");
				$(".audience-info-outer").removeClass("open").slideUp();
				$(".audience-tab." + myName).addClass("active");
				$("#mobile-audience-outer").after($(".audience-info-outer." + myName));
				$(".audience-info-outer." + myName).addClass("open").slideDown();
			}
		});
	}

	function audienceTabs480() {
		$(".audience-tab").off();
		$(".audience-tab").click(function(e) {
			e.preventDefault();
			if($(this).hasClass("active")) {
				$(this).removeClass("active");
			}
			var myName = $.trim( $(this).attr("class").replace("audience-tab","") );
			if($(".audience-info-outer." + myName).hasClass("open")) {
				$(".audience-tab." + myName).removeClass("active");
				$(".audience-info-outer." + myName).removeClass("open").slideUp();
			} else {
				$(".audience-tab").removeClass("active");
				$(".audience-info-outer").removeClass("open").slideUp();
				$(".audience-tab." + myName).addClass("active");
				$(this).after($(".audience-info-outer." + myName));
				$(".audience-info-outer." + myName).addClass("open").slideDown();
			}
		});
	}

	function hpVideoCont() {
		if($("#hp-alert .announcements div.ui-article").length) {
			$("#hp-alert-outer").show();
			var alertHeight = $("#hp-alert-outer").height();
			var winWidth = $(window).width();
			var winHeight = $(window).height() - alertHeight;
			$("#hp-video-outer").css({
				"width": winWidth + "px",
				"height": winHeight + "px"
			});
			$("#hp-video-player").css({
				"max-height": winHeight + "px"
			});
		} else {
			var winWidth = $(window).width();
			var winHeight = $(window).height();
			$("#hp-video-outer").css({
				"width": winWidth + "px",
				"height": winHeight + "px"
			});
			$("#hp-video-player").css({
				"max-height": winHeight + "px"
			});
		}
	}

	function pgScreenSize() {
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			audienceTabs1024();

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			audienceTabs768();

		} if (getWidth >= 640 && getWidth < 768) { // 640+
			audienceTabs768();

		} if (getWidth >= 480 && getWidth < 640) { // 480+
			appView();
			audienceTabs480();

		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
			audienceTabs480();
		}
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-outer div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 span.accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('');
				}
			}
		});

		//DOC accordion header click
		$("#hp-content-outer div.ui-widget-header, .sp.column.one div.ui-widget-header").off('click').click(function(){
			var clickedApp = $(this).parent();

			if($(clickedApp).hasClass('active')){
				$(clickedApp).removeClass('active');
				$('.accordion-handle', clickedApp).removeClass('point-down').addClass('point-left');
			}else{
				$("#hp-content-outer div.ui-widget.app.active .accordion-handle").removeClass('point-down').addClass('point-left');
				$("#hp-content-outer div.ui-widget.app.active").removeClass("active");

				$('.accordion-handle', clickedApp).removeClass('point-left').addClass('point-down');
				$(this).parent().addClass("active");
			}

			smoothScroll("#" + $(clickedApp).parent().attr("id"), 10);
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

