	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#gb-search input#sw-search-input").removeAttr("placeholder");
		$("div#gb-search input#sw-search-input").attr("value", "Search");
		$("div#gb-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#gb-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

	function calendarListView(){
		if($(window).width() < 640) {
			//DOC auto click function for schoolwires calendar list view for small mobile devices
			if(!$("a.ui-btn-toggle.list").hasClass("active")){
				$("a.ui-btn-toggle.list").click();
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};

	//usage
	//console.log(hexToRgb("#FFFFFF"));
	//usage with RGBA
	//console.log("rgba("+hexToRgb('#FFFFFF')+",0.5)");

