$.fn.headlinesRotate = function(){
  return this.each(function() {
    //CACHE ARTICLES LIs
    var $children = $(this).children();

    //CURRENT ELEMENT TO DISPLAY
    var position = -1;

    !function loop() {
        //GET NEXT ELEMENT POSITION
        //RESTART FROM FIRST LI IF LAST ONE IS ACTIVE
        position = (position + 1) % $children.length;
        //FADE ELEMENTS
        $children.eq(position).fadeIn(2000).delay(8000).fadeOut(2000, loop);
    }();
  });
};

// CHECK FOR IE AND SET IE VARIABLES
var isMSIE = 0, isMSIE7 = 0, isMSIE8 = 0, isMSIE9 = 0, isMSIE10 = 0, isMSIE11 = 0;
if(navigator.userAgent.indexOf("MSIE") > -1){ isMSIE = 1; }
if(navigator.userAgent.indexOf("MSIE 7") > -1){	isMSIE7 = 1; }
if(navigator.userAgent.indexOf("MSIE 8") > -1){	isMSIE8 = 1; }
if(navigator.userAgent.indexOf("MSIE 9") > -1){	isMSIE9 = 1; }
if(navigator.userAgent.indexOf("MSIE 10") > -1){ isMSIE10 = 1; }
if(navigator.userAgent.indexOf("MSIE 11") > -1){ isMSIE11 = 1; }

// GET BROWSER AND VERSION
function getBrowser(){
	var N = navigator.appName, ua=navigator.userAgent, tem;
	var M = ua.match(/(opera|chrome|safari|firefox|msie)\/?\s*(\.?\d+(\.\d+)*)/i);
	if(M && (tem= ua.match(/version\/([\.\d]+)/i))!= null) M[2] = tem[1];
	M = M ? [M[1], M[2]] : [N, navigator.appVersion, '-?'];
	return [M[0], M[1]];
}

var browser = getBrowser();
var browserName = browser[0].toLowerCase();
var browserVersion = Math.floor(parseInt(browser[1]));

//loads the IFrame Player API code asynchronously.
var tag = document.createElement('script');

tag.src = "https://www.youtube.com/iframe_api";
var firstScriptTag = document.getElementsByTagName('script')[0];
firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

//creates an  (and YouTube player)
//after the API code downloads.
var player;
var windowWidth;
var windowHeight;
var alertHeight;

function onYouTubeIframeAPIReady() {
	var mediaSelect = ('Streaming Video' == 'Streaming Video; Background Photo' ) ? 'Background Photo' : 'Streaming Video';
	if( mediaSelect == 'Streaming Video' && !$(".msie9").length ){
		if($("#hp-alert .announcements div.ui-article").length) {
			alertHeight = $("#hp-alert-outer").height();
			windowWidth = $(window).width();
			windowHeight = $(window).height();
			player = new YT.Player('player', {
				height: windowHeight,
				width: windowWidth,
				videoId: 'DhF5JJRq4Qk',
				playerVars: { 
					'modestbranding': 1,
				        'controls': 0, 
				        'autoplay': 1,
				        'rel' : 0,
				        'loop': 1,
				        'wmode': 'opaque',
				        'showinfo': 0
				},
				events: {
					'onReady': onPlayerReady,
					'onStateChange': onPlayerStateChange
				}
			});
		} else {
			windowWidth = $(window).width();
			windowHeight = $(window).height();
			player = new YT.Player('player', {
				height: windowHeight,
				width: windowWidth,
				videoId: 'DhF5JJRq4Qk',
				playerVars: { 
					'modestbranding': 1,
				        'controls': 0, 
				        'autoplay': 1,
				        'rel' : 0,
				        'loop': 1,
				        'wmode': 'opaque',
				        'showinfo': 0
				},
				events: {
					'onReady': onPlayerReady,
					'onStateChange': onPlayerStateChange
				}
			});
		}
	} else {
		$("#hp-video-outer").addClass("static-image");
	}
}
//the API will call this function when the video player is ready.
function onPlayerReady(event) {
	csVideoResize();
	$(window).resize(csVideoResize);
}

//The API calls this function when the players state changes.
//The function indicates that when playing a video (state=1),
//the player should play for six seconds and then stop.
var done = false;
function onPlayerStateChange(event) {
	if (event.data == YT.PlayerState.PLAYING && !done) {
		$(".cs-video-btn").click(function(e) {
			e.preventDefault();
			$(this).toggleClass("paused animate");
			pausePlayVideo();
		});
	} else {
		pausePlayVideo();
	}
}
function pausePlayVideo() {
	if($(".cs-video-btn").hasClass("paused")) {
		$(".cs-video-btn span").text("Play Video");
		$(".cs-video-btn").attr("aria-label","Play");
		player.pauseVideo();
	} else {
		$(".cs-video-btn span").text("Pause Video");
		$(".cs-video-btn").attr("aria-label","Pause");
		player.playVideo();
	}
}
function stopVideo() {
	player.pauseVideo();
}
function csVideoResize() {
	if($("#player").length) {
		var screenWidth = $(window).width();
		var screenHeight = $(window).height();
		var vidWidth = screenWidth, vidHeight = 0;

		// GET IMAGE RATIO
		var initW = 853.5, initH = 480;
		var ratio = initH / initW;
		vidHeight = vidWidth * ratio;

		// APPLY THE DIMENSIONS
		$("#player, #hp-video-player").css({"height": vidHeight + "px" , "width": screenWidth + "px"});
	}
}

