    $(document).ready(function(){
      var isTouchScreen = 'ontouchstart' in window,
        $hoverLink = $('#navigation .new-hover a'),
        $contentContainer = $('.red-menu-tab-content');

      var mainNavToggle = function (e) {
        e.preventDefault();
        $hoverLink.toggleClass('collapsed');
        $contentContainer.toggle();
      };

      if (!isTouchScreen) {
        $('#navigation .new-hover, .red-menu-tab-content').on('mouseenter mouseleave', mainNavToggle);
      } else {
        $('#navigation .new-hover').on('click', mainNavToggle);
      }

      $(document).on('touchstart click', function (e){
        if (
          !$contentContainer.is(e.target) &&
          $contentContainer.has(e.target).length === 0 &&
          !$hoverLink.is(e.target) &&
          $hoverLink.has(e.target).length === 0
        ){
          $contentContainer.hide();
          $hoverLink.removeClass('collapsed');
        }
      });

      // hamburger mobile menu for non-cms pages
      $("#topnav .navbar-toggle").click(function(){
        $(".navbar-collapse").slideToggle();
      });
    });

