      }
      else {
        $('.user-sub-menu').show();
      }
    });

    //this is to deal with SPA routes for admin UI - the menu needs to close when those routes are clicked
    $('#topnavbar').on('click', '.close-on-click', function() {
      $('#topnav').find('.navbar-toggle').click();
    });

    VSC.GlobalModuleFactory.makeHeaderDisappearer().bindEvents();

  });
}(window.VSC, window.jQuery));
