span.script:
processLogin = function() {
try {
dojo.xhrPost({
    url : '/names.nsf?login',
    handleAs : "text",
    preventCache : true,
    content: {
    	"UserName": dojo.byId('view:_id1:_id2:callback1:username').value,
    	"Password": dojo.byId('view:_id1:_id2:callback1:password').value
    },
    load : function(response, ioArgs) {
    		if (response.indexOf('reasonType')==-1) {
    			if (response.indexOf('action="/names.nsf?Login"')==-1){
    				document.cookie = "BackupSesID=; expires=Thu, 01-Jan-70 00:00:01 GMT";
    				window.location.href = '/elementary.nsf/Main.xsp';
    			} else {
    			if (document.cookie.indexOf('BackupSesId=')!=-1){
    									document.cookie = "DomAuthSessId=" + document.cookie.split('BackupSesID=')[1].substr(0, 32);
    								}
    								document.cookie = "BackupSesId=; expires=Thu, 01-Jan-70 00:00:01 GMT";
    			dojo.byId('view:_id1:_id2:callback1:loginErrorPanel').innerHTML = "03 You have entered an invalid username or password, or do not have sufficient credentials.";
    			}
    		} else {
    			if (document.cookie.indexOf('BackupSesId=')!=-1){
    									document.cookie = "DomAuthSessId=" + document.cookie.split('BackupSesID=')[1].substr(0, 32);
    								}
    								document.cookie = "BackupSesId=; expires=Thu, 01-Jan-70 00:00:01 GMT";
    			dojo.byId('view:_id1:_id2:callback1:loginErrorPanel').innerHTML = "02 You have entered an invalid username or password, or do not have sufficient credentials.";
    		}
    },
    error : function(response, ioArgs) {
        dojo.byId('view:_id1:_id2:callback1:loginErrorPanel').innerHTML = "01 You have entered an invalid username or password, or do not have sufficient credentials.";
    }
});
} catch(e) {
	alert(e.toString());
}
} //processLogin

