                    function loadScript(url, callback) {
                        var script = document.createElement("script")
                        script.type = "text/javascript";
                        if (script.readyState) { //IE
                            script.onreadystatechange = function() {
                                if (script.readyState == "loaded" ||
                                    script.readyState == "complete") {
                                    script.onreadystatechange = null;
                                    callback();
                                }
                            };
                        } else { //Other
                            script.onload = function() {
                                callback();
                            };
                        }
                        var host = document.location.host;
                        if (host.indexOf('stage') > -1 ) {
                                url = "https://finalstage.att.com" + "/scripts/goldeneye/staging/scripts/goldeneye.staging.js";
                        }else if (host.indexOf('prf') > -1 ) {
                                url = "https://prf3.att.com/scripts/goldeneye/perf/scripts/goldeneye.perf.js";
                        }else{
                            url = "https://www.att.com" + "/scripts/goldeneye/scripts/goldeneye.js";
                        }
                        if(host.indexOf('author') == -1){
	                        script.src = url;
	                        document.getElementsByTagName("head")[0].appendChild(script);
            			}
                    }
                    loadScript("", function() {
                        //initialization code
                    });
