link.script:
  window.dataLayer = window.dataLayer || [];
  function gtag(){dataLayer.push(arguments);}
  gtag('js', new Date());

  gtag('config', 'UA-105998787-1');

div.script:
(function (window, document, $, undefined) {
    $(function () {
        $('#cms-widget-b9c5bfe3-826d-42c1-94a7-2c573e62f540').on('click', '.meta-event-attachment-link', function (e) {
            var $target = $(e.currentTarget);
            var p = $.ajax({
                url: $target.attr('data-ajax'),
            });
            p.done(function (resp) {
                $(resp)
                    .appendTo($('body'))
                    .modal({
                        show: false
                    })
                    .on('show.bs.modal', function () {
                        $(this).css('display', 'block');
                        var $d = $(this).find('.modal-dialog'),
                            o = ($(window).height() - $d.height()) / 2,
                            bm = parseInt($d.css('marginBottom'), 10);
                        o = Math.max(o, bm);
                        $d.css('margin-top', o);
                    })
                    .on('hidden.bs.modal', function () {
                        $(this).remove();
                    })
                    .modal('show');
            });
            e.preventDefault();
        });
    });
})(window, document, jQuery);

