div.script:
jQuery(document).ready( function() {
	jQuery(document).keyup(function(e) {
		if(e.keyCode === 27) {
			jQuery( "div.uk-button-dropdown.uk-open" ).removeClass('uk-open');
		}
		if( e.keyCode === 13) {
			jQuery('.uk-nav-offcanvas > li')
			.first()
			.find('a')
			.first()
			.focus();
		}
	});

	jQuery( "div.uk-button-dropdown" ).focus(function() {
			const focusOnFirst = !jQuery(this).hasClass("uk-open");

			jQuery(this)
				.addClass( "uk-open" )
				.attr("aria-expanded","true");

			jQuery("div.uk-button-dropdown").not(this)
				.removeClass("uk-open")
				.attr("aria-expanded","false");

			if(focusOnFirst) {
				jQuery(this)
					.find("ul.uk-nav li a")
					.first()
					.focus();
			}
			else {
				jQuery(this)
					.removeClass("uk-open")
					.attr("aria-expanded", "false");
			}

	});

	jQuery( "ul.uk-nav li:last-child a").blur(function(){
		jQuery(this)
			.closest('div.uk-button-dropdown')
			.removeClass( "uk-open" )
			.attr("aria-expanded","false");
	});

	// ACCORDION FOCUS
	jQuery('.uk-accordion-content a').focus(function() {
		// find the parent accordion of this link, and if it's not already active, then expand it
		jQuery(this)
			.closest('.uk-accordion-content')
			.parent()
			.prev()
			.not('.uk-active')
			.trigger('click');
	});

	jQuery('.uk-accordion-content:last-child a:last-child').blur(function() {
		jQuery(this)
			.closest('.uk-accordion-content')
			.parent()
			.prev()
			.trigger('click');
	});

});

function TranslateTo(language)
{
  var currentURL = window.location.href;

  if (language.match('original')) {
    currentURL = $.getUrlVar('u');
    parent.location = unescape(currentURL);
  }
  else {
    if (currentURL.indexOf('translate.google.com') > 0)
      currentURL = unescape($.getUrlVar('u'));
      parent.location='http://translate.google.com/translate?hl=' + language + '&sl=auto&tl=' + language + '&u=' + encodeURIComponent(currentURL);
    }
  }

  (function($) {
    $(document).ready(function(){

      $.extend({
        getUrlVars: function(){
        var vars = [], hash;
        var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
        for(var i = 0; i < hashes.length; i++)
        {
          hash = hashes[i].split('=');
          vars.push(hash[0]);
          vars[hash[0]] = hash[1];
        }
        return vars;
      },
      getUrlVar: function(name){
        return $.getUrlVars()[name];
      }
    });

    if ($(".languageDropdown").length > 0) {
      var currentURL = window.location.href;
        if (currentURL.indexOf('translate.google.com') > 0)
          document.getElementById('gtl_original').style.display = 'inline-block';
        else
          document.getElementById('gtl_original').style.display = 'none';
    }
  });
})(jQuery);

EasyBlog.ready(function($){

	// Bind event's on the font size changer.
	$('[data-font-resize]').on('click', function() {

		// General font size
		var content = $('[data-blog-content]'),
			current = content.css('font-size'),
			num = parseFloat(current, 10),
			unit = current.slice(-2),
			operation = $(this).data('operation');

		// 
 tag size
		var pTag = content.find('p'),
			pNum = parseFloat(pTag.css('font-size'), 10);

		//  size
		var spanTag = content.find('span'),
			spanNum = parseFloat(spanTag.css('font-size'), 10);

		//  header tag
		var header = content.find(':header'),
			headerNum = parseFloat(header.css('font-size'), 10);

		// block link description text
		var linksBlock = $('[data-type="links"]'),
			linksDesc = linksBlock.find('.media-content');

		if (operation == 'increase') {
			num = num * 1.4;
			pNum = pNum * 1.4;
			spanNum = spanNum * 1.4;
			headerNum = headerNum * 1.4;
		}

		if (operation == 'decrease') {
			num = num / 1.4;
			pNum = pNum / 1.4;
			spanNum = spanNum / 1.4;
			headerNum = headerNum / 1.4;
		}

		content.css('font-size', num + unit);
		pTag.css('font-size', pNum + unit);
		spanTag.css('font-size', spanNum + unit);
		header.css('font-size', headerNum + unit);
		linksDesc.css('font-size', num + unit);
	});
});

EasyBlog.ready(function($) {

    $('[data-post-print]').on('click', function(event) {
        var el = $(this),
            url = el.attr('href');


        window.open(url, 'win2', 'status=no,toolbar=no,scrollbars=yes,titlebar=no,menubar=no,resizable=yes,width=640,height=480,directories=no,location=no');

        // Prevent bubbling up.
        event.preventDefault();
    });
});

