function handleTabControl(prev,last=null){
  var codeKey = event.keyCode || event.which;
  if (codeKey === 9) {
    if(event.shiftKey && prev) {
      event.preventDefault();
      document.querySelector(prev).focus();
    }
    else{
      if(last){
        event.preventDefault();
        document.querySelector(last).focus();
      }
    }
  }
}

