div.script:

  jQuery(function($) {  
      setTimeout(function(){  
          // Open all parents of a sidebar menu:  
          $('.uk-nav-side .uk-parent').addClass('uk-open');  
          $('.uk-nav-side .uk-parent > div').removeClass('uk-hidden');  
          $('.uk-nav-side .uk-parent > div').css('height','auto');  
      }, 500);  
  });
