div.script:
	function googleTranslateElementInit() {
		jQuery('#google-translate-placeholder').remove();
		new google.translate.TranslateElement({
			pageLanguage: 'en', 
			includedLanguages: 'af,ar,bg,bn,bs,de,es,fa,fr,gu,hi,hmn,id,it,iw,ja,kn,ko,ku,lo,ml,mn,mt,my,ne,pa,ro,ru,so,sq,sr,su,sw,ta,te,th,tl,uk,ur,vi,zh-CN,zh-TW', 
			layout: google.translate.TranslateElement.InlineLayout.SIMPLE
		}, 'google_translate_element');
	}
	jQuery(document).ready(function() {
		jQuery(window).breakpointEvents({
			defaultBreakpoint: 'xs',
			breakpoints: {
				'sm' : 750,
				'md' : 970,
				'lg' : 1170
			},
			modelSelector: '.container'
		});
	});
jQuery(window).on('bp:sm bp:md bp:lg', function() {
	console.log("xs google translate");
});
jQuery(window).on('bp:xs', function() {
	console.log("xs google translate");
});

