$.fn.followTo = function (pos) {
    var $this = this,
        $window = $(window);

    $window.scroll(function (e) {
        if ($window.scrollTop() > pos) {
            $this.css({
                position: 'fixed',
                top: 140
            });
        } else {
            $this.css({
                position: 'fixed',
                top: 300
            });
        }
    });
};

$('#quicklinks').followTo(300);

