            if (!away) {
        if($('#banner').length > 0) {
                away = true;
        
          if ($('.RadMenu_PageControls').length > 0) {
              $('#header').stop().css({ position : 'fixed' , top : '0px', borderBottom: '10px solid #006837' }).animate({backgroundColor: "#ffffff"}, 500);
            $('.headerinfo').css({ display : 'none'});
          }
          
          
          else {
          
              $('#header').stop().css('position' , 'fixed').animate({backgroundColor: "#ffffff", borderBottom: '10px solid #006837' }, 500);
            
          }
        

if ($('.RadMenu_PageControls').length > 0) {

                  $('#header').stop().css({ position : 'fixed' , top : '50px', left : '0px', borderBottom: '10px solid #006837'  }).animate({ backgroundColor: "#ffffff"}, 500);
                   

}

else {

                  $('#header').stop().css({ position : 'fixed' , top : '0px', left : '0px', borderBottom: '10px solid #006837'  }).animate({ backgroundColor: "#ffffff"}, 500);
                   $('.headerinfo').css({ display : 'none'});

}

          $('#header .logo').stop().animate({ maxWidth : '450px'}, 500);
        }
        
        
            }
        } else {
      
      if($('#theme').hasClass('homepage')) {
            away = false;
            $('#header').stop().css({ position : 'absolute', top: '0px', borderBottom: 'none' }).animate({backgroundColor: "transparent"}, 500);
            $('.headerinfo').css({ display : 'block'});
      $('#header .logo').stop().animate({ maxWidth : '550px'}, 500);


      }
    }
    });
  
  if(!$('#banner').length > 0) {
    if($('#theme').hasClass('homepage')) {
      $('#header').addClass('frozen');
                        $('#page').addClass('frozen');
    }
  }
        
});

 
